var book = {
	"name": "Mattheu",
	"numChapters": 28,
	"chapters": {
		"1": {
			"1": "Buka ya leloko la Jesu Kreste, mora Davida, mora Abrahama.",
			"2": "Abrahama a tswala Isaaka; Isaaka a tswala Jakobo; Jakobo a tswala Juda le banababo;",
			"3": "Juda a tswala Farese le Sara, ka Thamare; Farese a tswala Esrome; Esrome a tswala Arame;",
			"4": "Arame a tswala Aminadabe; Aminadabe a tswala Naasone; Nasone a tswala Salmone;",
			"5": "Salmone a tswala Boase, ka Rahaba; Boase a tswala Obede, ka Ruthe; Obede a tswala Jese;",
			"6": "Jese a tswala morena Davida.",
			"7": "Morena Davida a tswala Salome, ka mosadi wa Uria; Salomone a tswala Roboame; Roboame a tswala Abia; Abia a tswala Asa;",
			"8": "Asa a tswala Josafate; Josafate a tswala Jorame; Jorame a tswala Osiase;",
			"9": "Osiase a tswala Jothame; Jothame a tswala Akase; Akase a tswala Esekiase;",
			"10": "Esekiase a tswala Manasse; Manasse a tswala Amone; Amone a tswala Josiase;",
			"11": "Josiase a tswala Jekonia le banababo, mohla Bajuda ba iswang kgolehong Babilona.",
			"12": "Jekonia a tswala Salathiele, ha setjhaba se se se isitswe Babilona; Salathiele a tswala Sorobabele;",
			"13": "Sorobabele a tswala Abiude; Abiude a tswala Eliakime; Eliakime a tswala Asore;",
			"14": "Asore a tswala Sadoke; Sadoke a tswala Akime; Akime a tswala Eliude;",
			"15": "Eliude a tswala Eleasare; Eleasare a tswala Matthane; Matthane a tswala Jakobo;",
			"16": "Jakobo a tswala Josefa, monna wa Maria eo Jesu, ya bitswang Kreste, a tswetsweng ke yena.",
			"17": "Mme jwale, ho qala ka Abrahama, ho isa ho Davida, meloko yohle ke meloko e leshome le metso e mene; ho tloha ho Davida ho isa kgolehong ya Babilona, ke meloko e leshome le metso e mene; mme hape, ho tloha kgolehong ya Babilona ho isa ho Jesu Kreste, ke meloko e leshome le metso e mene.",
			"18": "Ho tswalwa ha Jesu Kreste ho tlile ka mokgwa ona: Maria, mmae, ha a lebeleditswe ke Josefa, a tshoha a emodisitswe ke matla a Moya o Halalelang, ba eso ho kopane.",
			"19": "Josefa, monna wa hae, kahobane e le motho ya lokileng, mme a sa rate ho mo hobosa, a hopola ho mo lahla ka sephiring.",
			"20": "Empa, eitse ha a sa hopotse teng, lengeloi la Morena la mo hlahela ka toro, la re: Josefa, mora Davida, o se ke wa tshaba ho inkela Maria, mosadi wa hao, hobane se emotsweng ke yena, ke sa Moya o Halalelang.",
			"21": "O tla tswala mora, mme o tla mo rea lebitso la Jesu; hobane ke yena ya tla pholosa setjhaba sa hae dibeng tsa sona.",
			"22": "Hoo hohle ho hlahile, hore ho etsahale ho boletsweng ke Morena, ka moporofeta ya itseng:",
			"23": "Bonang, morwetsana o tla emola, a tswale mora, mme lebitso la hae o tla bitswa Emmanuele, ke ho re, ha ho fetolwa, Modimo o na le rona.",
			"24": "Yare Josefa ha a tsoha borokong, a etsa kamoo lengeloi la Morena le mo laetseng kateng; a inkela mosadi wa hae;",
			"25": "mme ha a ka a mo tseba, a eso ho belehe mora hae wa letsibolo; mme a mo rea lebitso la Jesu."
		},
		"2": {
			"1": "Jesu eitse ha a tswaletswe Bethlehema wa Judea, mehleng ya morena Heroda, bonang, Ba Bohlale ba botjhabela ba fihla Jerusalema,",
			"2": "ba re: O kae ya tswetsweng morena wa Bajuda? Hobane re bone naledi ya hae botjhabela, mme re tlile ho mo kgumamela.",
			"3": "Yare ha morena Heroda a utlwa, a tshoha, le ba Jerusalema bohle ba tshoha le yena.",
			"4": "Hobane a bokelle baprista ba baholo kaofela le bangodi ba setjhaba, a ba botsa moo e ka kgonang Kreste a tswallwe teng.",
			"5": "Ba mmolella, ba re: Ke Bethlehema wa Judea; hobane ho ngodilwe jwalo ke moporofeta, ho thwe:",
			"6": "Le wena Bethlehema, fatshe la Juda, ha o monyenyane hara metse e meholo ya Juda, hobane ho tla tswa ho wena motsamaisi ya tla disa setjhaba sa ka sa Iseraele.",
			"7": "Yare hobane Heroda a bitse Ba Bohlale sephiring, a ba botsisisa nako eo naledi e bonahetseng ka yona;",
			"8": "mme a ba romela Bethlehema, a re: Eyang le botsisise ditaba tsa ngwana haholo; mme etlare ha le mo fumane, le ntsebise, le nna ke tle ke ye, ke mo kgumamele.",
			"9": "Yare ha ba utlwile morena, ba ya; ebile naledi eo ba e boneng botjhabela, e ba etella pele, ya fihla, ya ema hodima moo ngwana a leng teng.",
			"10": "Eitse ha ba bona naledi, ba nyakalla ka thabo e kgolo haholo.",
			"11": "Ha ba kena tlung, ba fumana ngwana le mmae, Maria; ba wa ka mangwele, ba mo kgumamela; mme ha ba phutholotse matlotlo a bona, ba mo ntshetsa dimpho tsa kgauta, le dibano, le mirra.",
			"12": "Eitse ha ba senoletswe ke Modimo ka toro, hore ba se ke ba boela ho Heroda, ba kgutlela lefatsheng la bona ka tsela esele.",
			"13": "Ha ba se ba tlohile, lengeloi la Morena la hlahela Josefa ka toro, la re: Tsoha, o nke ngwana le mmae, o balehele Egepeta, mme o dule teng, ke tle ke be ke o bolelle; hobane Heroda o tla batla ngwana ho mmolaya.",
			"14": "Yaba Josefa o tsoha, a nka ngwana le mmae bosiu, a fallela Egepeta.",
			"15": "A dula teng, Heroda a ba a shwa, ho tle ho etsahale ho boletsweng ke Morena ka moporofeta ya itseng: Ke latile mora wa ka Egepeta.",
			"16": "Jwale, Heroda ha a bona hobane o phomilwe ke ba bohlale, a halefa haholo; a romela ho bolaya bana bohle ba Bethlehema le ba naha yohle ya teng, ba lemo tse pedi le ba ba hlahlamang, a lekanya ka nako eo a e botsisisitseng ba bohlale.",
			"17": "Mohlang oo, ho etsahetse ho boletsweng ke moporofeta Jeremia, ha a re:",
			"18": "Mohoo o utlwahetse Rama, wa pohomela, wa dillo, wa mahlomola a maholo: Ragele o llela bana ba hae, mme o hana ho tshediswa, hobane ba le siyo.",
			"19": "Empa, eitse hobane Heroda a shwe, lengeloi la Morena la hlahela Josefa ka toro, Egepeta, la re:",
			"20": "Tsoha, o nke ngwana le mmae, mme o kgutlele lefatsheng la Iseraele; hobane ba neng ba batla moya wa ngwana, ba se ba shwele.",
			"21": "A tsoha, a nka ngwana le mmae, a tla lefatsheng la Iseraele.",
			"22": "Empa, eitse hobane a utlwe ha ho thwe, Arkelause o busa Judea, tulong tsa ntatae Heroda, a tshaba ho ya teng; mme ha a senoletswe ke Modimo ka toro, a kgutlela naheng ya Galelea.",
			"23": "Ha a fihla teng, a aha motseng o bitswang Nasaretha, ho tle ho etsahale ho boletsweng ke baporofeta, ho thwe: O tla bitswa Monasarene."
		},
		"3": {
			"1": "Mehleng eo, ha hlaha Johanne Mokolobetsi, a ruta feelleng la Judea.",
			"2": "O ne a re: Bakang, hobane mmuso wa mahodimo o atametse.",
			"3": "Hobane ke yena ya neng a lekanntswe ke moporofeta Esaia, ha a re: Lentswe la ya howang feelleng, le re: Hlekang mmila wa Morena, le lokise ditsela tsa hae.",
			"4": "Johanne o ne a apere kobo ya boya ba kamele, a itlamile letheka ka leqhaama la letlalo; dijo tsa hae e ne e le ditsie le dinotshi tsa naha.",
			"5": "Yaba ba Jerusalema, le ba Judea yohle, le ba naha yohle e pela Jordane, ba tswela ho yena,",
			"6": "mme ba kolobetswa ke yena Jordane, ba ntse ba ipolela dibe tsa bona.",
			"7": "Empa, yare ha a bona bafarisi le basaddusi ba bangata ba tla kolobetsong ya hae, a ba bolella, a re: Madinyane a marabe, le tshositswe ke mang ho balehela bohale bo tlang?",
			"8": "Ha ho le jwalo he, behang ditholwana tse tshwanelang pako.",
			"9": "Mme le se ke la re ka ho lona: Abrahama ke ntata rona; hobane ke a le bolella, ke re: Modimo o ka tsosetsa Abrahama bana le ka majwe ana.",
			"10": "Selepe se se se beilwe metsong ya difate; sefate ka seng se sa beheng tholwana tse molemo, se tla rengwa, se lahlelwe mollong.",
			"11": "Haele nna, ke le kolobelletsa pako ka metsi feela; empa ya tlang ka morao ho nna, o mpheta ka matla, mme ha ke a lokela ho mo nkela dieta; ke yena ya tla le kolobetsa ka Moya o Halalelang le ka mollo.",
			"12": "Seoloso sa hae se letsohong la hae; o tla fiela seotlo sa hae ruri, a bokelle koro ya hae modikong, mooko wona a o tjhese mollong o sa timeng.",
			"13": "Mohlang oo, Jesu a tswa Galelea, a tla Jordane ho Johanne ho kolobetswa ke yena.",
			"14": "Empa Johanne a hana ho yena, a re: Ke nna ke tshwanetseng ho kolobetswa ke wena, mme o tla ho nna na?",
			"15": "Empa Jesu a mo araba, a re: Ntumelle jwale; hobane e ka kgona re phethe jwalo ho lokelang hohle. Yaba Johanne o a mo lesa.",
			"16": "Eitse Jesu ha a kolobeditswe, a phakisa a tswa metsing, mme hang, mahodimo a phetlelwa yena; a bona Moya wa Modimo o theoha a ka leeba, o tla hodima hae.",
			"17": "Mme ha utlwahala lentswe le tswang mahodimong, le re: Enwa ke Mora wa ka ya ratwang, eo ke kgahliswang ke yena."
		},
		"4": {
			"1": "Yaba Jesu o iswa ke Moya feelleng, ho lekwa ke Diabolose.",
			"2": "A dula a itimile dijo ka matsatsi a mashome a mane le ka masiu a mashome a mane, a ntoo lapa.",
			"3": "Yaba moleki o mo atamela, a re: Ha o le Mora Modimo, ere, majwe ana a fetohe mahobe.",
			"4": "Empa Jesu a araba, a re: Ho ngodilwe, ho thwe: Motho a ke ke a phela ke bohobe feela, empa o tla phela le ke polelo tsohle tse tswang molomong wa Modimo.",
			"5": "Jwale, Diabolose a mo nkela motseng o halalelang, a mmea hlorong ya tempele,",
			"6": "a re ho yena: Ekare ha o le Mora wa Modimo, o itihele fatshe; hobane ho ngodilwe, ho thwe: O tla o laella mangeloi a wona, mme a tla o kgakeletsa matsohong a wona, hore leoto la hao le se ke la kgoptjwa ke lejwe.",
			"7": "Jesu a re ho yena: Ho ngodilwe hape, ho thwe: O se ke wa leka Morena, Modimo wa hao.",
			"8": "Diabolose a boela a mo nkela thabeng e telele haholo, a mmontsha mebuso yohle ya lefatshe le kganya ya yona,",
			"9": "a re ho yena: Ntho tseo kaofela ke tla o nea tsona, ha o ka wa ka mangwele, wa nkgumamela.",
			"10": "Jwale, Jesu a re ho yena: Tloha, Satane, hobane ho ngodilwe, ho thwe: Kgumamela Morena, Modimo wa hao, mme o sebeletse yena feela.",
			"11": "Jwale, Diabolose a mo tlohela; mme bonang, mangeloi a atamela Jesu, a mo sebeletsa.",
			"12": "Eitse Jesu ha a utlwile hobane Johanne o kenntswe teronkong, a boela Galelea;",
			"13": "a falla Nasaretha, a ya aha Kapernauma, motse o pela lewatle, naheng ya Sabulone le Nefthali;",
			"14": "hore ho etsahale ho boletsweng ke moporofeta Esaia, ha a re:",
			"15": "Lefatshe la Sabulone le lefatshe la Nefthali, tseleng ya lewatle, mose wa Jordane, Galelea wa ditjhaba,",
			"16": "setjhaba se neng se dutse lefifing, se bone lesedi le leholo; mme lesedi le tjhabetse ba neng ba dutse naheng ya lefu le moriting wa lona.",
			"17": "Ho tloha mohlang oo, Jesu a qala ho ruta, a re: Bakang, hobane mmuso wa mahodimo o atametse.",
			"18": "Jesu, ha a sika le lewatle la Galelea, a bona bara ba babedi ba motho a le mong, e le Simone, ya bitswang Petrose, le Andrease, ngwanabo, ba akgela matlowa a bona lewatleng; hobane e ne e le ba tshwasang dihlapi.",
			"19": "A re ho bona: Ntateleng, mme ke tla le etsa ba tshwasang batho.",
			"20": "Yaba ba phakisa ba tlohela matlowa a bona, ba mo latela.",
			"21": "Yare ha a fetela pele, a bona ba babedi ba bang, bara ba motho a le mong, e le Jakobo, mora wa Sebedea, le Johanne, ngwanabo, ba dutse sekepaneng le Sebedea, ntata bona, ba rokahanya matlowa a bona; mme a ba bitsa.",
			"22": "Yaba hang ba tlohela sekapana le ntata bona, ba latela Jesu.",
			"23": "Jesu a ya le Galelea yohle, a ntse a ruta masinagogeng a teng, a bolela Evangeli ea mmuso, a fodisa malwetse wohle le mefokolo yohle e ho batho.",
			"24": "Botumo ba hae ba utlwahala Siria yohle; ha tliswa ho yena bohle ba seng monate ke malwetse a mefutafuta, le ba tshwerweng ke mafu, le ba nang le bademona, le ba jewang ke sethwathwa, le ba omeletseng ditho; mme a ba fodisa.",
			"25": "Mme matshwele a maholo a tswang Galelea, le Dekapolise, le Jerusalema, le Judea, le mose wane wa Jordane, a mo latela."
		},
		"5": {
			"1": "Jesu, ha a bona matshwele, a nyolohela thabeng; mme hobane a dule, barutuwa ba hae ba mo atamela.",
			"2": "Yaba o ahlamisa molomo wa hae, a ba ruta, a re:",
			"3": "Ho lehlohonolo bafumanehi moyeng, hobane mmuso wa mahodimo ke wa bona.",
			"4": "Ho lehlohonolo ba llang; hobane ba tla tshediswa.",
			"5": "Ho lehlohonolo ba bonolo; hobane ba tla ja lefa la lefatshe.",
			"6": "Ho lehlohonolo ba lapelang ho loka, ba nyoretsweng hona; hobane ba tla kgoriswa.",
			"7": "Ho lehlohonolo ba mohau; hobane ba tla hauhelwa.",
			"8": "Ho lehlohonolo ba pelo di hlwekileng; hobane ba tla bona Modimo.",
			"9": "Ho lehlohonolo ba etsang kgahiso; hobane ba tla rewa bana ba Modimo.",
			"10": "Ho lehlohonolo ba hlorisetswang ho loka; hobane mmuso wa mahodimo ke wa bona.",
			"11": "Le tla ba lehlohonolo, ha ba le hlapaola ka baka la ka, ba le hlorisa, ba le bea bobe bohle ka bohata.",
			"12": "Thabang, le hlasimolohe ka ho nyakalla; hobane moputso wa lona o tla ba moholo lehodimong; hobane ba hlorisitse jwalo baporofeta ba bileng teng pele ho lona.",
			"13": "Le letswai la lefatshe; empa ekare ha letswai le tapa, le ka nokwa kang? Ha le sa na mosebetsi, le se le ka mpa la lahlelwa kantle, la hatwa ke batho.",
			"14": "Le lesedi la lefatshe. Motse o beilweng hodima thaba, ha o na ho patwa.",
			"15": "Lebone le lona ha le hoteletswe ho kenngwa katlasa morula, empa e le ho hlongwa kandelareng, le tle le bonesetse bohle ba leng tlung.",
			"16": "Lesedi la lona le ke le kganyetse batho jwalo, ba tle ba bone mesebetsi ya lona e molemo, mme ba rorise Ntata lona ya mahodimong.",
			"17": "Le se ke la hloma ekaba ke tlile ho fedisa molao, leha e le baporofeta; ha ke a tla ho fedisa, ke mpa ke tlile ho phetha.",
			"18": "Hobane kannete ke a le bolella, ke re: Lehodimo le lefatshe ha di eso ho fete, jota leha e le nngwe, le hlaku leha e le nngwe ya molao, e ke ke ya feta, ho eso ho etsahale ntho tsohle.",
			"19": "Ka baka leo, ya tlolang o mong o monyenyane wa melao eo, mme a ruta batho ho etsa jwalo, o tla bitswa e monyenyane mmusong wa mahodimo; empa ya e bolokang, mme a ruta batho ho e boloka, yena o tla bitswa e moholo mmusong wa mahodimo.",
			"20": "Hobane ke a le bolella, ke re: Ho loka ha lona ha ho sa fetise ha bangodi le ha bafarisi, le ke ke la kena mmusong wa mahodimo.",
			"21": "Le utlwile hoba ho boleletswe baholo, ha thwe: O se ke wa bolaya; mme, ya bolaileng o tla tshwanelwa ke kahlolo.",
			"22": "Empa nna, ke re ho lona: Motho, leha e ka ba mang le mang, ya halefelang ngwanabo feela, o tla tshwanelwa ke kahlolo; ya reng ho ngwanabo: Raka, o tla tshwanelwa ke kahlolo ya lekgotla; mme ya reng ho yena: O lehlanya, o tla tshwanelwa ke mollo wa dihele.",
			"23": "Ka baka leo, ha o hlahisa kabelo ya hao aletareng, mme teng o hopola hobane ngwaneno o na le taba le wena,",
			"24": "siya kabelo ya hao moo, pela aletare, o ye ho boelana le ngwaneno pele, o ntano tla hlahisa kabelo ya hao.",
			"25": "O phakise o dumellane le ya tsekisanang le wena, le sa le tseleng le yena; esere mohlomong ya tsekisanang le wena a o neela maohlodi, mme moahlodi a o neela mohlanka wa kahlolo, mme wa kgarameletswa teronkong.",
			"26": "Kannete ke a o bolella, ke re: O ke ke wa tswa teng o eso ho lefe tjheletana e horeletseng.",
			"27": "Le utlwile hoba ho boleletswe baholo, ha thwe: O se ke wa feba.",
			"28": "Empa nna, ke re ho lona: Motho, leha e ka ba mang le mang, ya tadimang mosadi ho mo lakatsa, o se a mo febile pelong ya hae.",
			"29": "Ekare ha leihlo la hao le letona le o dihela, o le honye, mme o le lahlele hole le wena; hobane ho ka ba molemo ho wena ha setho sa hao se timela se le seng feela, mmele wohle wa hao o se ke wa lahlelwa diheleng.",
			"30": "Ekare ha letsoho la hao le letona le o dihela, o le pome, mme o le lahlele hole le wena; hobane ho ka ba molemo ho wena ha setho sa hao se timela se le seng feela, mmele wohle wa hao o se ke wa lahlelwa diheleng.",
			"31": "Ho boletswe hape, ha thwe: Motho ha a hlala mosadi wa hae, a mo nee lengolo la hlalo.",
			"32": "Empa nna, ke re ho lona: Motho, leha e ka ba mang le mang, ya hlalang mosadi wa hae, haese ka baka la bofebe, o mmea hore a fetohe mofebi; mme e mong le e mong ya nyalang mosadi ya hladilweng, o a feba.",
			"33": "Hape, le utlwile hoba ho boleletswe baholo, ha thwe: O se ke wa kweneha kanong ya hao, o mpe o nee Morena seo o mmaletseng sona ka kano.",
			"34": "Empa nna, ke re ho lona: Le se ke la hlapanya le ho leka; leha e le ka lehodimo, hobane ke terone ya Modimo;",
			"35": "leha e le ka lefatshe, hobane ke bonamelo ba maoto a wona; leha e le ka Jerusalema, hobane ke motse wa morena e moholo.",
			"36": "O se ke wa ana le ka hlooho ya hao, hobane qoba ya moriri, leha e le nngwe, o ka sitwa ho e fetola bosweu, leha e le botsho.",
			"37": "Empa puo ya lona e be e, e, tjhe, tjhe; se eketswang se tswa ho e mobe.",
			"38": "Le utlwile hoba ho boletswe, ha thwe: Leihlo le phetetswe ka leihlo, leino ka leino.",
			"39": "Empa nna, ke re: Le se ke la hanela ya le etsang hampe; empa ekare ha motho a o jabela lerama le letona, o mo sekehele le lona le leng.",
			"40": "Motho, ha a rata ho o tsekisa le ho o amoha kojwana, o mo tlohelle le yona kobo.",
			"41": "Ekare ha motho a o qobella, hore o tsamaye le yena meile e le nngwe, o tsamaye di le pedi.",
			"42": "Nea ya qelang ho wena, o se ke wa furalla ya ratang ho adima ho wena.",
			"43": "Le utlwile hoba ho kile ho thwe: Rata wa heno, o hloye eo e leng sera ho wena.",
			"44": "Empa nna, ke re ho lona: Ratang bao e leng dira ho lona, le hlohonolofatse ba le rohakang; ba le hloileng le ba etse hantle, le rapelle ba le tsohelang matla le ba le hlorisang;",
			"45": "le tle le be bana ba Ntata lona ya mahodimong; hobane o tjhabisetsa ba babe le ba batle letsatsi la hae, mme o nesetsa ba lokileng le ba kgopo.",
			"46": "Hobane ha le rata ba le ratang, le ka ba le moputso ofe? Na balekgetho ha ba etse jwalo le bona na?",
			"47": "Mme ha le dumedisa banababo lona feela, le etsa ho fetisang hofe? Balekgetho le bona ha ba etse jwalo na?",
			"48": "Mme jwale, phethehang, jwalokaha Ntata lona ya mahodimong a phethehile."
		},
		"6": {
			"1": "Iponeleng, le tle le se ke la hlahisa ho loka ha lona pontsheng ya batho, hore le bonwe ke bona; ha ho le jwalo, ha le ka ke la newa moputso ke Ntata lona ya mahodimong.",
			"2": "Ka baka leo, ha o etsa thuso, o se ke wa letsa terompeta pela hao, jwalokaha ho etswa ke baikaketsi ka masinagogeng le diterateng, ba tle ba roriswe ke batho. Kannete ke a le bolella, ke re: Ba se ba nkile moputso wa bona.",
			"3": "Empa wena, ha o etsa thuso, letsoho la hao le letshehadi le se ke la tseba se etswang ke le letona la hao,",
			"4": "hore thuso ya hao e be sephiring; mme Ntatao ya bonang sephiring, o tla o putsa.",
			"5": "Leha le rapela, le se ke la ba jwaloka baikaketsi, ba ratang ho rapela ba eme masinagogeng le dikoung tsa diterata, hore ba bonwe ke batho. Kannete ke a le bolella, ke re: Ba se ba nkile moputso wa bona.",
			"6": "Empa wena, ha o rapela, kena phaposing ya hao, mme ha o kwetse monyako wa hao, o rapele Ntatao ya sephiring; mme Ntatao ya bonang sephiring, o tla o putsa.",
			"7": "Ha le rapela, le se ke la touta jwaloka bahetene; hobane ba hloma ba tla utluwa ke ho bua haholo.",
			"8": "Empa le se ke la tshwana le bona; hobane Ntata lona o tseba seo le se hlokang, le eso ho qele ho yena.",
			"9": "Jwale lona, rapelang jwalo, le re: Ntata rona ya mahodimong, lebitso la hao a le ke le kgethehe;",
			"10": "ho tle mmuso wa hao; thato ya hao e etswe lefatsheng, jwalokaha e etswa lehodimong;",
			"11": "o re fe kajeno bohobe ba rona ba tsatsi le leng le le leng;",
			"12": "o re tshwarele melato ya rona, jwalokaha re tshwarela ba nang le melato ho rona;",
			"13": "o se ke wa re isa molekong; o mpe o re lwele ho e mobe; [hobane mmuso ke wa hao, le matla, le kganya ka ho se feleng. Amen!]",
			"14": "Hobane ekare ha le tshwarela batho ditlolo tsa bona, Ntata lona ya mahodimong o tla le tshwarela le lona;",
			"15": "empa ekare ha le sa tshwarele batho ditlolo tsa bona, Ntata lona le yena a ke ke a le tshwarela ditlolo tsa lona.",
			"16": "Ha le itima dijo, le se ke la sosobanya difahleho tsa lona, jwaloka baikaketsi; ba itshenya tshobotsi, ba tle ba bonahale ho batho hobane ba itima dijo. Kannete ke a le bolella, ke re: Ba se ba nkile moputso wa bona.",
			"17": "Haele wena, enore ha o itima dijo, o itlotse hlooho, o iphotle sefahleho;",
			"18": "o tle o se ke wa bonahala ho batho hobane o itima dijo, ho mpe ho tsejwe ke Ntatao ya sephiring; mme Ntatao ya bonang sephiring, o tla o putsa.",
			"19": "Le se ke la ipokellela matlotlo lefatsheng, moo tshwele le mafome a jang teng, le moo mashodu a fatang teng, a utswang.",
			"20": "Le mpe le ipokellele matlotlo lehodimong, moo tshwele le mafome a sa jeng teng, le moo ho seng mashodu a fatang, a utswang.",
			"21": "Hobane moo letlotlo la hao le leng teng, ke moo le pelo ya hao e tla ba teng.",
			"22": "Lebone la mmele ke leihlo; ka baka leo, ha leihlo la hao le lokile, mmele wohle wa hao o tla ba le lesedi.",
			"23": "Empa ekare ha leihlo la hao le le lebe, mmele wohle wa hao o tla ba lefifing. Mme jwale, ha lesedi le leng ho wena e le lefifi, lefifi leo e tla ba le lekakang!",
			"24": "Ha ho motho ya ka tsebang ho sebeletsa marena a mabedi: hobane kapa o tla hloya e mong, a rate e mong; kapa o tla kgomarela e mong, a nyedise e mong. Le ka sitwa ho sebeletsa Modimo le Mamona.",
			"25": "Ke moo ke reng ho lona: Le se ke la belaela ka tsa ho phela ha lona, ka seo le tla se ja, le ka seo le tla se nwa; leha e le ka sa mmele ya lona, ka seo le tla se apara. Na bophelo ha bo fetise dijo na? Na mmele ha o fetise seaparo na?",
			"26": "Tadimang dinonyana tsa lehodimo; hobane ha di jale, ha di kotule, ha di bokelle medikong; empa Ntata lona ya mahodimong o a di fepa. Ha ke re, lona le di fetisa haholo na?",
			"27": "Mme ke mang ho lona ya ka eketsang boemo ba hae ka setswe se le seng ka ho belaella teng?",
			"28": "Le ka baka la diaparo le belaela keng? Lemohang mokgwa oo dikgahla tsa naha di melang ka wona; ha di sebetse, ha di ohle;",
			"29": "empa ke a le bolella hoba esita le Salomone, kganyeng yohle ya hae, ha a ka a ba le seaparo se jwaloka sa e nngwe ya tsona.",
			"30": "Modimo ha o apesa jwalo jwang ba naha, bo bang teng kajeno, mme hosasane bo se bo tla lahlelwa seboping, na haholo o ke ke wa le apesa, lona batho ba tumelo e nyenyane?",
			"31": "Ke moo ke reng: Le se ke la belaela ka ho re: Re tla jang? kapa, re tla nwang? kapa, re tla ikapesa kang?",
			"32": "Hobane ntho tseo tsohle ke tse batlwang ke bahetene; empa Ntata lona ya mahodimong o tseba hoba le hloka dintho tseo tsohle.",
			"33": "Le mpe le batle mmuso wa Modimo le ho loka ha wona pele; mme dintho tseo tsohle le tla di ekeletswa.",
			"34": "Ka baka leo, le se ke la belaella tsa hosasane; hobane hosasane ho tla ipatlela se sa hona. Letsatsi ka leng le lekanwa ke tsietsi ya lona."
		},
		"7": {
			"1": "Se ahloleng, le tle le se ke la ahlolwa;",
			"2": "hobane le tla ahlolwa ka kahlolo eo le ahlolang ba bang ka yona; mme tekanyo eo le lekanyang ba bang ka yona, le tla lekanyetswa ka yona le lona.",
			"3": "O tadimelang lehlokwana le leihlong la ngwaneno, athe ha o lemohe tshiya e ka leihlong la hao?",
			"4": "Kapa, o ka bolella ngwaneno jwang, wa re: Lesa, ke ntshe lehlokwana leihlong la hao, kanthe o na le tshiya leihlong la hao?",
			"5": "Moikaketsi, qala hle ka ho ntsha tshiya e leihlong la hao, ke moo o tla bontshisa ho ntsha lehlokwana leihlong la ngwaneno.",
			"6": "Se neeng dintja dintho tse halalelang, le se ke la lahlela diperela tsa lona pela dikolobe, esere mohlomong tsa di hatakela ka maoto, tsa ntoo sokolohela ho lona, tsa le harola.",
			"7": "Kopang, mme le tla newa; batlang, mme le tla fumana; kokotang, mme le tla bulelwa.",
			"8": "Hobane e mong le e mong ya kopang, o a newa; ya batlang o a fumana, mme ho bulelwa ya kokotang.",
			"9": "Ana motho ke ofe ho lona ya ka neang mora wa hae lejwe, ha a kopile bohobe?",
			"10": "Kapa ha a kopile hlapi, na a ka mo nea noha na?",
			"11": "Mme lona, ba lonya, ha le tseba ho nea bana ba lona ntho tse molemo, Ntata lona ya mahodimong o tla fetisa hakakang ka ho nea ba kopang ho yena tse molemo!",
			"12": "Mme jwale, ntho tsohle tseo le ratang hore batho ba le etse tsona, ba etseng tsona le lona; hobane ke wona molao le baporofeta.",
			"13": "Kenang ka monyako o patisaneng; hobane monyako wa timelo o hebehebe, le tsela e yang teng e phatlaletse, mme ba kenang teng ba bangata.",
			"14": "Hobane monyako wa bophelo o patisane, le tsela e yang teng e tshesane, mme ba e fumanang ha ba bakae.",
			"15": "Phemang baporofeta ba bohata, ba tlang ho lona ba apere kobo ya nku, empa kahare e le diphiri tse hapakang.",
			"16": "Le tla ba tseba ka ho beha ha bona. Ana morara o kgolwa meutlweng, kapa a difeiye di kgolwa dibajweng na?",
			"17": "Efela sefate ka seng se monate se beha ditholwana tse monate; le sefate ka seng se sebe se beha ditholwana tse mpe.",
			"18": "Sefate se monate se ka sitwa ho beha ditholwana tse mpe, mme sefate se sebe se ka sitwa ho beha ditholwana tse monate;",
			"19": "Sefate se seng le se seng se sa beheng ditholwana tse monate, se tla rengwa, se lahlelwe mollong.",
			"20": "Ke moo ke reng: Le tla ba tseba ka ho beha ha bona.",
			"21": "Hase bohle ba reng ho nna: Morena! Morena! ba tlang ho kena mmusong wa mahodimo; empa ho tla kena ya etsang thato ya Ntate ya mahodimong.",
			"22": "Tsatsing leo, ba bangata ba tla re ho nna: Morena, Morena, na ha re a ka ra porofeta ka lebitso la hao na? Na ha re a ka ra leleka bademona ka lebitso la hao na? Na ha re a ka ra etsa mehlolo e mengata ka lebitso la hao na?",
			"23": "Mohlang oo, ke tla ba bolella pepeneneng, ke re: Ha ke a ka ka le tseba le mehla; ntloheleng, lona ba sebetsang bokgopo.",
			"24": "Ka baka leo, e mong le e mong ya mamelang dipolelo tseo tsa ka, mme a etsa ka tsona, nka mo tshwantsha ka monna ya bohlale, ya hahileng ntlo ya hae letlapeng;",
			"25": "mme pula ya theoha, phororo tsa tla, le meya ya foka, ya otla ntlo eo; mme ha e a ka ya wa, kahobane motheo wa yona o le letlapeng.",
			"26": "Empa e mong le e mong ya mamelang dipolelo tseo tsa ka, mme a sa etse ka tsona, a ka tshwantshwa ka monna ya lewatla, ya hahileng ntlo ya hae lehlabatheng;",
			"27": "mme pula ya theoha, phororo tsa tla, le meya ya foka, ya otla ntlo eo, mme ya wa, mme ho wa ha yona ya eba ho hoholo.",
			"28": "Yare ha Jesu a qetile dipolelo tseo, matshwele a tsota thuto ya hae;",
			"29": "hobane a ne a ba ruta jwaloka motho ya nang le borena, e seng jwaloka bangodi ba bona."
		},
		"8": {
			"1": "Yare ha a theohile thabeng, matshwele a mangata a mo latela.",
			"2": "Mme bonang, ha tla molepera ho yena, a mo kgumamela, a re: Morena, ha o rata, o na le matla a ho nntlafatsa.",
			"3": "Jesu a nanabetsa letsoho, a mo ama, a re: Ke a rata, o ntlafale. Yaba lepera la hae le fola hang.",
			"4": "Jesu a re ho yena: O iponele, o se ke wa bolella motho; o mpe o ye, o ipontshe ho moprista, mme o hlahise mpho e laetsweng ke Moshe, e tle e be bopaki ho bona.",
			"5": "Eitse ha Jesu a kena Kapernauma, molaodi wa lekgolo a tla ho yena, a mo rapela,",
			"6": "a re: Morena, mohlanka wa ka o rapaletse tlung; o omeletse ditho, mme o kula hampe.",
			"7": "Jesu a re ho yena: Ke tla ya, ke mo fodise.",
			"8": "Empa molaodi wa lekgolo a bua, a re: Morena, ha ke na tokelo ya hobane o kene tlung ya ka; o mpe o bue polelo feela, mme mohlanka wa ka o tla fola.",
			"9": "Hobane le nna, ke leng motho ya buswang ke ba bang, ke na le bahlabani ba katlase ho nna; mme ha ke re ho e mong: Eya, o a ye; le ho e mong: Tloo, o a tle; le ho mohlanka wa ka: Etsa hoitseng, o a ho etse.",
			"10": "Jesu, ha a mo utlwa, a tsota, mme a bolella ba mo latelang, a re: Kannete ke a le bolella, ke re: Ha ke eso ho bone tumelo e kalo, le Iseraeleng.",
			"11": "Ke a le bolella, ke re: Ba bangata ba tla tswa botjhabela le bophirimela, mme ba tla dula dijong le Abrahama, le Isaaka, le Jakobo mmusong wa mahodimo.",
			"12": "Empa bara ba mmuso ba tla lahlelwa lefifing le kantle, moo dillo di tla ba teng le ditsikitlano tsa meno.",
			"13": "Yaba Jesu o re ho molaodi wa lekgolo: Eya, o etsetswe kamoo a dumetseng kateng. Mme mohlanka wa hae a fola nakong yona eo.",
			"14": "Jesu ha a etla tlung ya Petrose, a fumana mohwehadi wa hae a robetse, a jewa ke febere.",
			"15": "Yare ha a mo amile letsoho, febere ya mo tlohela, mme a tsoha, a mo sebeletsa.",
			"16": "Yare mantsiboya, ha tliswa ho yena ba bangata ba nang le batemona, a leleka meya ho bona ka polelo feela; a fodisa bohle ba seng monate;",
			"17": "hore ho etsahale ho boletsweng ke moporofeta Esaia, ha a re: O inketse mefokolo ya rona, o ipelesitse malwetse a rona.",
			"18": "Jesu ha a bona hobane matshwele a maholo a mo teetse hare, a laela barutuwa hore ba mo tshedisetse mose.",
			"19": "Ebile e mong, e leng mongodi, o mo atamela, a re ho yena: Moruti, moo o yang teng, leha e le kae le kae, ke tla o latela.",
			"20": "Jesu a re ho yena: Diphokojwe di na le mesima, le dinonyana tsa lehodimo di na le matlo; empa Mora motho ha a na moo a ka samelang hlooho ya hae teng.",
			"21": "E mong wa barutuwa a re ho yena: Morena, ntumelle, ke ye ho epela ntate pele.",
			"22": "Empa Jesu a re ho yena: Ntatele, o lese bafu ba epele bafu ba bona.",
			"23": "Eitse ha a kene sekepaneng, barutuwa ba hae le bona ba kena.",
			"24": "Mme bonang, ha tsoha sefefo se seholo lewatleng, sekepana sa ba sa apeswa ke maqhubu a metsi; empa Jesu yena o ne a robetse.",
			"25": "Yaba barutuwa ba hae ba mo atamela, ba mo tsosa, ba re: Morena, re pholose; re a timela!",
			"26": "A re ho bona: Batho ba tumelo e nyenyane, le boi keng na? Yaba o ema, a kgalemela meya le lewatle; mme kgutso e kgolo ya eba teng.",
			"27": "Batho bao ba tsota haholo, ba re: Enwa ke ya jwang, ha a utluwa le ke meya le lewatle?",
			"28": "Yare ha a fihlile mose wane, naheng ya Bagadarene, a kgahlanyetswa ke batho ba babedi ba nang le batemona, ba tswa mabitleng; e ne e le ba hlanyang hampe, hoo ho neng ho se motho ya ka betang pelo ho feta ka tsela eo.",
			"29": "Ba howa, ba re: Taba ya rona le wena keng, Mora wa Modimo? Na o tlile mona ho re hlokofatsa, e eso ho be nako ya teng na?",
			"30": "Mme hojana le bona, mohlape o moholo wa bo o le teng, wa dikolobe tse fulang.",
			"31": "Batemona ba mo rapela, ba re: Ha o re leleka mona, o re romele mohlapeng wa dikolobe.",
			"32": "A re ho bona: Eyang. Yaba ba tswa, ba ya dikolobeng; mme hang, mohlape wohle wa dikolobe wa mathela selomong, wa itihela lewatleng, mme tsa eshwa metsing.",
			"33": "Yaba badisa ba baleha; ha ba fihla hae, motseng, ba bolela ditaba tseo tsohle, le tse hlahetseng ba neng ba ena le batemona.",
			"34": "Yaba ba motse kaofela ba tswela ho kgahlanyetsa Jesu, mme ha ba mmona, ba mo rapela haholo, hore a tlohe naheng ya bona."
		},
		"9": {
			"1": "Jesu a kena sekepaneng, a boela a tshela, mme a tla motseng wa habo.",
			"2": "Yaba o tlisetswa motho ya omeletseng ditho, a rapaletse diphateng; Jesu ha a bona tumelo ya bona, a re ho ya omeletseng ditho: Beta pelo, ngwana ka, dibe tsa hao o di tshwaretswe.",
			"3": "Yaba ba bang ho bangodi ba re ka ho bona: Enwa o a nyefola.",
			"4": "Jesu ha a bona mehopolo ya bona, a re: Le hopolelang bobe ka dipelong tsa lona?",
			"5": "Hobane ke hofe ho leng bonolo, na ke ho re: O tshwaretswe dibe, kapa a ke ho re: Ema, o tsamaye, na?",
			"6": "Mme jwale — hore le tsebe hobane Mora motho o na le matla lefatsheng a ho tshwarela batho dibe — a re ho ya omeletseng ditho: Ema, o nke diphate tsa hao, o ikele tlung ya hao.",
			"7": "Yaba o a ema, a ikela tlung ya hae.",
			"8": "Bongata ha bo bona hoo, ba tsota, mme ba rorisa Modimo, kahobane o neile batho matla a makalo.",
			"9": "Jesu ha a tloha teng, a bona motho ya bitswang Mattheu, a dutse pela ntlo ya lekgetho; yaba o re ho yena: Ntatele. Mattheu a ema, a mo latela.",
			"10": "Yare ha a dutse dijong tlung, balekgetho ba bangata le baetsadibe ba kena, ba dula dijong le Jesu le barutuwa ba hae.",
			"11": "Bafarisi, ha ba bona hoo, ba re ho barutuwa ba hae: Na moruti wa lona o jelang le balekgetho le baetsadibe?",
			"12": "Jesu ha a utlwa, a re ho bona: Ba phelang hantle ha ba batle ngaka, empa e batlwa ke ba kulang.",
			"13": "Empa eyang, le ithute polelo e reng: Ke rata mohau, e seng sehlabelo; hobane ha ke a tla ho biletsa ba lokileng pakong, empa e le baetsadibe.",
			"14": "Mohlang oo, barutuwa ba Johanne ba tla ho Jesu, ba re: Rona le bafarisi, re itimelang dijo hangata, athe barutuwa ba hao ha ba itime tsona?",
			"15": "Jesu a re ho bona: Balekane ba monyadi na ba ka lla, monyadi ha a sa le ho bona na? Empa nako e a tla eo monyadi a tla tloswa ho bona, ke hona ba tla itima dijo.",
			"16": "Ha ho motho ya ka beang setsiba se setjha seaparong se tsofetseng, hobane setsiba se kenntsweng se ka hahola seaparo, sa eketsa taboho.",
			"17": "Hape, batho ha ba tshele veine e ntjha makukeng a kgale; hobane makuka a ka phatloha, mme jwale veine ya tsholoha, makuka le wona a timela. Veine e ntjha e mpa e tshelwa makukeng a matjha, mme ntho tse pedi tseo di a bolokeha.",
			"18": "A re a sa ba bolella ditaba tseo, hang, kgosana ya fihla, ya mo kgumamela, ya re: Moradi wa ka o tswa shwa hona jwale; empa tloo, o bee seatla sa hao hodima hae, mme o tla boela a phele.",
			"19": "Yaba Jesu o a tloha, a ya le yena, le barutuwa ba hae.",
			"20": "Jwale, mosadi ya neng a ntse a kgathatswa ke phallo ya madi, dilemo di se di le leshome le metso e mmedi, a mo hlaha kamorao, a ama qola ya kobo ya hae.",
			"21": "Hobane o ne a re ka ho yena: Ha nka ama kobo ya hae feela, ke tla fola.",
			"22": "Jesu a reteleha, a mo tadima, a re ho yena: Beta pelo, moradi wa ka; tumelo ya hao e o pholositse. Mme nakong yona eo, mosadi a fola.",
			"23": "Jesu ha a fihla tlung ya kgosana, a bona ba teang diphala le batho ba bangata ba etsang lerata le leholo.",
			"24": "A re ho bona: Tlohang, hobane morwetsana ha a shwa, o mpa a robetse. Mme ba mo tsheha.",
			"25": "Yare ha a ntsheditse bongata boo ntle, a kena, a mo tshwara ka letsoho, mme morwetsana a tsoha.",
			"26": "Mme taba eo ya tuma lefatsheng leo lohle.",
			"27": "Jesu ha a tloha moo, difofu tse pedi tsa mo latela, tsa howa, tsa re: Mora Davida, o re utlwele bohloko.",
			"28": "Eitse ha a kena tlung, difofu tseo tsa tla ho yena, mme Jesu a re ho tsona: Na le a dumela hore nka tseba ho etsa hoo na? Tsa re ho yena: E, Morena.",
			"29": "Yaba o ama mahlo a tsona, a re: Le etsetswe kamoo le dumetseng kateng.",
			"30": "Mahlo a tsona a tutuboloha; mme Jesu a di laya haholo, a re: Iponeleng, hore taba eo e se ke ya tsejwa ke motho.",
			"31": "Empa ha di tswile, tsa hasanya botumo ba hae naheng eo yohle.",
			"32": "Tsa re di sa tswa tloha, Jesu a tlisetswa motho ya semumu, ya nang le motemona.",
			"33": "Mme ha ho lelekilwe motemona semumu sa bua; bongata ba makala, ba re: Taba e jwalo ha e eso ho ka e bonwa Iseraeleng!",
			"34": "Empa bafarisi ba re: O leleka batemona ka morena wa batemona.",
			"35": "Jesu a tsamaya, a tjhalakela metse yohle le metsana, a ruta masinagogeng a teng, a bolela Evangeli ya mmuso, a fodisa malwetse wohle le mefokolo yohle.",
			"36": "Ha a bona matshwele, a a utlwela bohloko, hobane ba ne ba kgathetse, ba saretswe jwaloka dinku tse se nang modisa.",
			"37": "E bile moo a reng ho barutuwa ba hae: Kotulo e kgolo ruri, empa basebetsi ha ba bakae.",
			"38": "Rapelang ke hona Morena wa kotulo, hore a romele basebetsi kotulong ya hae."
		},
		"10": {
			"1": "Yare ha a bileditse ho yena barutuwa ba hae ba leshome le metso e mmedi, a ba nea matla a ho hlola meya e ditshila, ho e lahlela ntle, le ho fodisa malwetse wohle le mefokolo yohle.",
			"2": "Mabitso a baapostola ba leshome le metso e mmedi, ke ana: Wa pele, ke Simone, ya bitswang Petrose, le Andrease, ngwanabo; Jakobo, mora Sebedea, le Johanne, ngwanabo;",
			"3": "Filipi le Bartholome; Thomase le Mattheu, molekgetho; Jakobo, mora Alfea, le Thaddea;",
			"4": "Simone, Mokanana, le Judase Iskariota, yena ya ekileng Jesu.",
			"5": "Jesu a roma ba leshome le metso e mmedi bao, a ba laela, a re: Le se ke la ya ho baditjhaba, mme le se ke la kena motseng ofe kapa ofe wa Basamaria;",
			"6": "le mpe le ye dinkung tsa ntlo ya Iseraele tse timetseng.",
			"7": "Ha le ntse le tsamaya, le bolele, le re: Mmuso wa mahodimo o atametse.",
			"8": "Fodisang ba kulang, le tsose bafu, le ntlafatse balepera, le lahlele batemona ntle; le neilwe feela, neang feela.",
			"9": "Le se ke la inkela kgauta, leha e le silefera, leha e le tshepe e kgubedu ka maqhaameng a lona;",
			"10": "leha e le mokotla wa tsela, le dikobo tse pedi, le dieta, le lere; hobane ya sebetsang o lokelwa ke dijo tsa hae.",
			"11": "Ha le kena motseng, kapa motsaneng ofe le ofe, le botse hore na ke ofe wa teng ya nang le tshwanelo; le dule ha hae le be le tlohe.",
			"12": "Enore ha le kena tlung, le e dumedise.",
			"13": "Ekare ha ntlo eo e ena le tshwanelo, kgotso ya lona a e tle ho yona; empa ha e se na tshwanelo, kgotso ya lona e kgutlele ho lona.",
			"14": "E mong le e mong ya sa le amoheleng, le polelo tsa lona a sa di utlwe, ha le etswa tlung eo, kapa motseng oo, le hlohlore lerole la maoto a lona.",
			"15": "Kannete ke re ho lona: Kotlo ya ba lefatshe la Sodoma le la Gomorra e tla ba tjhatsi ho ya motse oo, ka tsatsi la kahlolo.",
			"16": "Bonang, ke le roma le le jwaloka dinku hara diphiri; ka baka leo, le be bohlale jwaloka dinoha, mme le be ba se nang mano jwaloka maebanakgorwana.",
			"17": "Mme le tsohele batho masene; hobane ba tla le isa kgotla, ba le shape masinagogeng a bona.",
			"18": "Hape, le tla iswa pela babusisi, le pela marena, ka baka la ka, le be dipaki tsa ka ho wona le ho ditjhaba.",
			"19": "Empa mohla ba le neelang bona, le se ke la belaella mokgwa oo le tla bua ka wona, le seo le tla se bolela; hobane seo le tla se bolela, le tla se newa nakong yona eo.",
			"20": "Hobane e ke ke ya eba lona le buang, empa e tla ba Moya wa Ntata lona o buang ka lona.",
			"21": "Ngwanabo motho o tla neela ngwanabo polaong, le ntata motho a etse ngwana hae jwalo; bana ba tla tsohela batswadi ba bona matla, ba ba bolaye.",
			"22": "Le tla hlouwa ke bohle ka baka la lebitso la ka; empa ya tiisetsang ho isa qetellong, ke yena ya tla bolokeha.",
			"23": "Etlere ha ba le hlorisa motseng o mong, le balehele ho o mong; hobane kannete ke re ho lona: Mora motho o tla hlaha, le eso ho qete metse yohle ya Iseraele.",
			"24": "Morutuwa ha a moholo ho moruti wa hae, le mohlanka ha a moholo ho morena wa hae.",
			"25": "Ho a lekana ha morutuwa a etswa jwaloka moruti wa hae, le mohlanka jwaloka morena wa hae. Ha ba bitsitse mong a ntlo Beelsebule, ba ntlo ya hae ba tla bitswa jwalo ho fetisa hakakang!",
			"26": "Le se ke la ba tshaba ke hona, hobane ha ho letho le kwahetsweng le sa tlo ka le kwahollwa; mme ha ho letho le sephiring le sa tlo ka le tsejwa.",
			"27": "Seo ke le bolellang sona lefifing, le se bolele leseding; seo le se utlwang le se nyenyeletswa tsebeng, le se bolele dihlorong tsa matlo.",
			"28": "Le se ke la tshaba ba bolayang mmele, ba mpa ba se na matla a ho bolaya moya; le mpe le tshabe ya nang le matla a ho bolaela moya le mmele diheleng.",
			"29": "Na dirobele tse pedi ha di rekwe ka peni e le nngwe na? Athe le se seng sa tsona se ke ke sa wela fatshe, kantle ho Ntata lona.",
			"30": "Esita le qoba tsa moriri wa hlooho tsa lona di badilwe kaofela.",
			"31": "Ka baka leo, le se ke la tshaba: le fetisa dirobele tse ngata.",
			"32": "E mong le e mong ya tla mpolela pela batho, le nna ke tla mmolela pela Ntate ya mahodimong;",
			"33": "empa e mong le e mong ya tla ntatola pela batho, le nna ke tla mo latola pela Ntate ya mahodimong.",
			"34": "Le se ke la hloma ekaba ke tlile ho romela kgotso lefatsheng; ha ke a tla ho romela kgotso, empa e le lerumo.",
			"35": "Hobane ke tlile ho fapanya motho le ntatae, moradi le mmae, ngwetsi le matsala yona;",
			"36": "le bafo ba motho e tla ba dira ho yena.",
			"37": "Ya ratang ntatae, leha e le mmae haholo ho nna, ha a ntshwanele; le ya ratang mora hae, leha e le moradi wa hae haholo ho nna, ha a ntshawanele;",
			"38": "mme ya sa nkeng sefapano sa hae, a ntatela, ha a ntshwanele.",
			"39": "Ya bolokang bophelo ba hae, o tla timellwa ke bona; empa ya lahlehelwang ke bophelo ba hae ka baka la ka, o tla be a bo bone.",
			"40": "Ya le amohelang o a nkamohela; mme ya nkamohelang o amohela ya nthomileng.",
			"41": "Ya amohelang moporofeta, kahoba a ena le lebitso la moporofeta, o tla newa moputso wa moporofeta; le ya amohelang ya lokileng, kahoba a ena le lebitso la ya lokileng, o tla newa moputso wa ya lokileng.",
			"42": "E mong le e mong ya nosang e mong wa ba banyenyane bana mohope feela wa metsi a batang, kahoba a ena le lebitso la morutuwa wa ka, kannete ke re ho lona: a ke ke a timellwa ke moputso wa hae."
		},
		"11": {
			"1": "Eitse ha Jesu a qetile ho laela barutuwa ba hae ba leshome le metso e mmedi, a tloha moo ho ya ruta le ho bolela Evangeli metseng ya naha eo.",
			"2": "Johanne ha a le teronkong, a utlwa mesebetsi ya Kreste; yaba o romela barutuwa ba hae ba babedi ho Jesu,",
			"3": "a re ho yena: Ana ke wena ya boletsweng, ha thwe, o tla tla, kapa re sa lebeletse e mong na?",
			"4": "Jesu a araba, a re ho bona: Eyang, le tsebise Johanne tseo le di utlwang, le tseo le di bonang:",
			"5": "Difofu di a bona, dihlotsa li a tsamaya, balepera ba ntlafala, ba thibaneng ditsebe ba utlwa, bafu ba tsoha, mme mafutsana a bolellwa Evangeli.",
			"6": "Ho lehlohonolo ya sa tlo ka a kgoptjwa ho nna.",
			"7": "Bao ha ba tlohile, Jesu a qala ho bolella bongata tsa Johanne, a re: Le ne le ile ho bonang felleng? Na e ne e le lehlaka le tsukutlwang ke moya na?",
			"8": "Le mpa le ne le ile ho bonang na? Na e ne e le motho ya apereng kobo tse bonolo na? Bonang, ba apereng tse bonolo ba matlung a marena.",
			"9": "Ana le ne le ile ho bonang? A e ne e le moporofeta na? E, ke rialo ho lona, ya fetisang le moporofeta.",
			"10": "Hobane ke yena ya bolelwang moo ho ngodilweng, ho thwe: Bona, ke roma pela sefahleho sa hao lenqosa la ka le tlang ho o lokisetsa tsela.",
			"11": "Ke re ho lona: Kannete, hara ba tswetsweng ke basadi, ha ho e moholo ho Johanne Mokolobetsi; empa e monyenyane mmusong wa mahodimo o moholo ho yena.",
			"12": "Ho tloha mehleng ya Johanne Mokolobetsi, ho fihlela jwale, mmuso wa mahodimo o kenwa ka matla, mme ba bohale ba o hapa.",
			"13": "Hobane baporofeta bohle le molao ba porofetile, ha ba ha fihla Johanne.",
			"14": "Mme ha le rata ho amohela polelo ya ka, ke yena Elia ya neng a tshwanetse ho tla.",
			"15": "Ya nang le ditsebe ho utlwa u utlwe.",
			"16": "Empa moloko wona na ke tla o tshwantsha kang? O jwaloka bana ba dutseng mapatlelong, ba howanang le thaka tsa bona,",
			"17": "ba re: Re le teetse phala, mme ha le a ka la etsa tjhao; re le binetse mahlomola, mme ha le a ka la lla.",
			"18": "Hobane Johanne o tlile a sa je, a sa nwe, mme ba re: O na le motemona.",
			"19": "Mora motho yena o tlile a eja, a enwa, mme ba re: A le ke le boneng motho wa moji le wa monwi, molekane wa balekgetho le wa baetsadibe; empa bohlale bo ipaketse ke bana ba jona.",
			"20": "Mohlang oo, Jesu a qala ho omanya metse eo a entseng mehlolo e mengata ho yona, kahobane e sa ka ya baka, a re:",
			"21": "Ho madimabe wena, Korasine! ho madimabe wena, Bethsaida! hobane mehlolo e entsweng ha eno, hola ya be e entswe Tire le Sidone, metse eo e ka be e bakile kgale, ka masela a mahwashe le ka molora.",
			"22": "Ka baka leo, ke re ho lona: Kotlo ya Tire le ya Sidone e tla ba tjhatsi ho ya lona ka tsatsi la kahlolo.",
			"23": "Le wena Kapernauma, ya phahamiseditsweng lehodimong, o tla kokobeletswa nqalong ya bafu; hobane mehlolo e entsweng ha eno, hola ya be e entswe Sodoma, motse oo o ka be o sa le teng le kajeno.",
			"24": "Ka baka leo, ke re ho wena: Kotlo ya lefatshe la Sodoma e tla ba tjhatsi ho ya hao ka tsatsi la kahlolo.",
			"25": "Nakong eo, Jesu a bua, a re: Ke a o rorisa, Ntate, Morena wa lehodimo le wa lefatshe, hobane taba tsena o di patetse ba bohlale le ba masene, mme o di kwaholletse ba banyenyane.",
			"26": "E, Ntate, ho jwalo, hobane ke kamoo o ithatetseng kateng.",
			"27": "Ntho tsohle di beilwe ho nna ke Ntate; mme ha ho ya tsebang Mora, haese Ntate; hape, ha ho ya tsebang Ntate, haese Mora, le eo Mora a ratang ho mo kwahollela yena.",
			"28": "Tloong ho nna, lona bohle ba kgathetseng, ba imelwang, mme ke tla le imolla!",
			"29": "Inkeleng joko ya ka, mme le ithute ho nna; hobane ke mosa le pelo e nolo, mme le tla fumanela meya ya lona phomolo.",
			"30": "Hobane joko ya ka e tjhatsi, le phahlo ya ka ha e na boima."
		},
		"12": {
			"1": "Mehleng eo, Jesu o ne a feta hara masimo ka tsatsi la Sabatha Barutuwa ba hae ba ne ba lapile, mme ba qala ho kga diqwapi, ba di ja.",
			"2": "Bafarisi ha ba bona hoo, ba re ho yena: Bona, barutuwa ba hao ba etsa ho sa dumellweng ho etswa ka tsatsi la Sabatha.",
			"3": "Empa a re ho bona: Na ha le a ka la bala ho entsweng ke Davida, mohla a neng a lapile le ba nang le yena;",
			"4": "kamoo a kileng a kena tlung ya Modimo kateng, a ja mahobe a pontsha, a neng a sa lokele ho jewa ke yana le ke ba nang le yena, e le a baprista ba nnotshi?",
			"5": "Kapa ha le a ka la bala molaong hobane, ka matsatsi a Sabatha, baprista ba tlola molao wa Sabatha tempeleng, mme ha ba na molato?",
			"6": "Mme ke re ho lona: Ho teng mona ho hoholo ho tempele.",
			"7": "Hola la tseba ho lekanntsweng ha ho thwe: ke rata mohau, e seng sehlabelo, le ka be le sa ka la tswa ba se nang molato.",
			"8": "Hobane Mora motho ke morena le wa Sabatha.",
			"9": "Ha a tloha teng, a kena sinagogeng ya bona.",
			"10": "Motho o ne a le teng moo, ya letsoho le omeletseng; ba botsa Jesu, ba re: Ana ho ka lokela ho fodisa ka tsatsi la Sabatha na? Ba rialo, ba batla sebaka sa ho mo qosa.",
			"11": "Empa yena a re ho bona: Motho ke ofe ho lona ya nang le nku e le nngwe, ekareng ha e wela sekoting ka Sabatha, a ke keng a e tshwara,",
			"12": "Athe motho o fetisa nku hakakang ka boholo! Ka baka leo, ho loketse ho etsa hantle ka Sabatha.",
			"13": "Yaba o re ho motho eo: Otlolla letsoho la hao. A le otlolla, mme la fola, la boela la loka jwaloka le leng.",
			"14": "Bafarisi ha ba tswile, ba mo rera, kamoo ba ka mmolayang kateng.",
			"15": "Empa Jesu a tseba, mme a tloha teng; matshwele a maholo a mo latela, mme a fodisa bohle ba kulang.",
			"16": "A ba laya, a re, ba se ke ba mmolela;",
			"17": "ho tle ho etsahale ho boletsweng ke moporofeta Esaia, ha a itse:",
			"18": "Bonang mohlanka wa ka eo ke ikgethetseng yena, moratuwa wa ka, eo moya wa ka o kgahliswang ke yena; ke tla bea Moya wa ka hodima hae, mme o tla bolella ditjhaba ho loka.",
			"19": "Ha a ka ke a omana; a ke ke a peroha; ha ho ya tla utlwa lentswe la hae diterateng;",
			"20": "ha a ka ke a roba lehlaka le tenyetsehileng, a ke ke a tima lebonenyana le kubellang, e be a etse hore ho loka ho hlole.",
			"21": "Mme ditjhaba di tla tshepa lebitso la hae.",
			"22": "Yaba ho tliswa ho Jesu motho ya nang le motemona, e le sefofu se semumu, a mo fodisa; mme jwale, sefofu se semumu sa bua, le hona sa bona.",
			"23": "Mme bongata bohle ba hlollwa, ba re: Ana enwa hase mora Davida na?",
			"24": "Empa bafarisi, ha ba utlwa taba eo, ba re: Enwa ha a leleka batemona, ke ka Beelsebule feela, morena wa batemona.",
			"25": "Athe Jesu a tseba mehopolo ya bona, mme a re ho bona: Mmuso o mong le o mong o kgaohaneng ho itwantsha o tla fetoha lesupi; le motse o mong le o mong, leha e le ntlo e kgaohaneng ho itwantsha, e ke ke ya tiya.",
			"26": "Satane ha a leleka Satane, eba o kgaohane ho itwantsha; ana mmuso wa hae o ka tiya jwang?",
			"27": "Ha ke fela ke leleka batemona ka Beelsebule, bara ba lona, bona, ba ba leleka ka mang? E tla ba bona baahlodi ba lona ke hona.",
			"28": "Empa ekare ha ke leleka batemona ka Moya wa Modimo, le tsebe hoba mmuso wa Modimo o le fihletse ruri.",
			"29": "Kapa motho a ka kena jwang tlung ya senatla, ho phahla mesebetsi ya sona, ha a sa ka a tlama senatla seo pele, ebe moo a phahlang ntlo ya sona?",
			"30": "Ya sa ntwaneleng, o a ntwantsha; le ya sa bokolleng le nna, o a hasanya.",
			"31": "Ke moo ke reng ho lona: Batho ba ka tshwarelwa sebe se seng le se seng le nyefolo e nngwe le e nngwe; empa nyefolo e nyefotseng Moya, batho ba ke ke ba e tshwarelwa.",
			"32": "Ha motho a sebile Mora motho, a ka tshwarelwa; empa ha a sebile Moya o Halalelang, a ke ke a tshwarelwa, leha e le mehleng ena, leha e le ho e tlang ho tla.",
			"33": "Kapa ereng, sefate se setle, tholwana ya sona le yona e ntle; kapa ereng, sefate se sebe, tholwana ya sona le yona e mpe; hobane sefate se tsejwa ka tholwana ya sona.",
			"34": "Madinyane a marabe, le ka bolela taba tse molemo jwang, le ena le lonya? Hobane molomo o buiswa ke seo pelo e tletseng sona.",
			"35": "Motho ya molemo o ntsha dintho tse molemo letlotlong le molemo la pelo ya hae; mme motho ya lonya o ntsha dintho tse lonya letlotlong le lonya la pelo ya hae.",
			"36": "Mme ke re ho lona: Ka tsatsi la kahlolo, batho ba tla tsekiswa polelo e nngwe le e nngwe ya bosawana eo ba e boletseng.",
			"37": "Hobane o tla bewa ya lokileng ka dipolelo tsa hao, mme ka dipolelo tsa hao o tla tsuwa.",
			"38": "Lekgatheng leo, bangodi le bafarisi ba bang ba re ho Jesu: Moruti, re rata re ka o bona o etsa mohlolo.",
			"39": "Empa a ba araba, a re: Moloko ona o lonya, o mofebi, o batla mohlolo; empa ha o ka ke wa newa mohlolo, haese mohlolo wa moporofeta Jonase.",
			"40": "Hobane jwaloka Jonase ha a bile matsatsi a mararo le masiu a mararo ka mpeng ya hlapi e kgolo, Mora motho le yena o tla ba maleng a lefatshe matsatsi a mararo le masiu a mararo.",
			"41": "Banna ba Ninive ba tla ema kahlolong le moloko ona, ba o tswe, hobane ba kile ba baka ka mokgosi wa Jonase; kanthe e moholo ho Jonase o teng mona.",
			"42": "Mofumahadi wa Borwa o tla ema kahlolong le moloko wona, a o tswe; hobane o ne a tlohe pheletsong tsa lefatshe, ho tla mamela bohlale ba Salomone; mme bonang, e moholo ho Salomone o teng mona.",
			"43": "Moya o ditshila, ha o tswile ho motho, o ikela dithoteng tse tjheleng ho batla phomolo, o mpe o e hloke.",
			"44": "Ke moo o reng: A nke ke boele tlung ya ka eo ke tswileng ho yona. Mme ere ha o fihla teng, o e fumane e le feela, e fietswe, e bile e hlophilwe.",
			"45": "Jwale o ye, o late meya e meng e supileng, e o fetisang ka bolotsana, mme e kene, e dule teng; mme taba tsa motho eo tsa ho fela di fetise tsa ho qala ka ho dula hampe. Moloko o lonya ona o tla hlahelwa ke taba e jwalo.",
			"46": "Eitse ha a sa bua le bongata, ha hlaha mmae le banababo, ba ema kantle, ba batla ho bua le yena.",
			"47": "Motho e mong a re ho Jesu: Mmao le banabeno ke bana kantle, ba batla ho bua le wena.",
			"48": "Empa yena a araba ya mmolellang, a re: Mmè ke mang, mme banabeso ke bafe?",
			"49": "Yaba o alatsa barutuwa ba hae ka letsoho, a re: Bonang, mme le banabeso ke bana.",
			"50": "Hobane e mong le e mong ya tla etsa thato ya Ntate ya mahodimong, ke yena e leng ngwaneso, le kgaitsedi ya ka le mmè."
		},
		"13": {
			"1": "Ka lona tsatsi leo, Jesu a tswa tlung, a dula pela lewatle.",
			"2": "Yaba matshwele a maholo a phuthehela ho yena, mme Jesu a kena sekepaneng, a dula teng; bongata bohle ba ema lebopong.",
			"3": "Yaba o ba bolella ditaba tse ngata ka ditshwantsho, a re: Bonang, mojadi o ne a tswile ho ya jala.",
			"4": "Eitse ha a jala, hlaku tse ding tsa wela pela tsela, mme dinonyana tsa tla, tsa di ja kaofela.",
			"5": "Hlaku tse ding tsa wela majweng, moo di se nang mobu o mongata, mme tsa phakisa ho mela, hobane di se na botebo ba mobu.",
			"6": "Letsatsi ha le tjhaba, tsa pona; mme tsa etjha ke ho hloka metso.",
			"7": "Tse ding tsa wela hara meutlwa, mme meutlwa ya hola, ya di pupetsa.",
			"8": "Empa tse ding tsa wela mobung o motle, mme tsa beha dijo; hlaku e nngwe ya beha tse lekgolo, e nngwe ya beha tse mashome a tsheletseng, e nngwe ya beha tse mashome a mararo.",
			"9": "Ya nang le ditsebe ho utlwa, a utlwe.",
			"10": "Barutuwa ba tla, ba re ho yena: Na o buelang le bona ka ditshwantsho na?",
			"11": "A ba araba, a re: Ke hobane lona le neilwe ho tseba diphiri tsa mmuso wa mahodimo, empa bona ha ba ka ba newa.",
			"12": "Hobane ya nang le ntho, o tla newa, mme ho tla tlala ho yena; empa ya se nang letho, o tla amohuwa le seo a nang le sona.",
			"13": "Ke ka baka leo ke buang le bona ka ditshwantsho, kahobane leha ba bona, ha ba bone; leha ba utlwa, ha ba utlwe, mme ha ba utlwisise.",
			"14": "Ho phethahala ho bona ho porofetilweng ke Esaia, ha a re: Ka kutlo le tla utlwa, empa le ke ke la utlwisisa; ka pono le tla bona, empa le ke ke la bontshisa.",
			"15": "Hobane pelo ya setjhaba sena e nonne; ditsebe tsa bona di utlwa ka thata, mme ba pompaditse mahlo a bona, hore ba se ke ba bona ka mahlo, ba se ke ba utlwa ka ditsebe, ba se ke ba utlwisisa ka dipelo, ba se ke ba sokoloha, mme ke se ke ka ba fodisa.",
			"16": "Empa mahlo a lona a lehlohonolo, hobane a bona, le ditsebe tsa lona di lehlohonolo, hobane di a utlwa.",
			"17": "Kannete ke re ho lona: Baporofeta ba bangata le ba lokileng ba bangata ba lakaditse ho bona tseo le di bonang, mme ha ba ka ba di bona, le ho utlwa tseo le di utlwang, mme ha ba ka ba di utlwa.",
			"18": "Jwale lona, mamelang taba ya setshwantsho sa mojadi.",
			"19": "E mong le e mong ha a utlwa polelo ya mmuso, mme a sa e utlwisise, e mobe o ye a tle ho yena, a mo amohe se jetsweng pelong ya hae. Eo ke ya amohetseng peo pela tsela.",
			"20": "Ya amohetseng peo majweng, ke motho ya utlwang lentswe, ya phakisang a le amohela ka thabo;",
			"21": "a mpa a se na motso ka ho yena, empa e le wa motsotso o se okae feela; ereha tshwenyeho le hloriso di hlaha ka baka la lentswe, a phakise a kgoptjwe.",
			"22": "Haele ya e amohetseng hara meutlwa, ke ya utlwang lentswe; empa ho belaella dintho tsa lefatshe lena, le ho thetsa ha lefumo, ho pupetsa lentswe, mme le nyope.",
			"23": "Empa ya amohetseng peo mobung o motle, ke motho ya utlwang lentswe, ya le utlwisisang, mme o a beha; hlaku e nngwe e hlahisa tse lekgolo, e nngwe e hlahisa tse mashome a tsheletseng, e nngwe e hlahisa tse mashome a mararo.",
			"24": "Jesu a ba beela setshwantsho se seng, a re: Mmuso wa mahodimo o tshwantshwa ka motho ya jadileng peo e ntle tshimong ya hae.",
			"25": "Yare ha ho robetswe ke batho, ya mo hloileng a tla, mme a jala mofoka hara koro, a ntoo ikela.",
			"26": "Mme koro ha e se e hlahisitse lekgaba, e se e tswetse, mofoka le wona wa bonahala.",
			"27": "Bahlanka ba mong a ntlo ba tla, ba re ho yena: Morena, ha re re, o ne o jadile peo e ntle tshimong ya hao? Na mofoka oo e o nkile kae?",
			"28": "A re ho bona: Ke ketso ya sera. Bahlanka ba re ho yena: Na ha o rate ha re ya kga mofoka oo na?",
			"29": "A re: Tjhe, mohlomong ha le ekga mofoka, le ka tshoha le fothola le yona koro.",
			"30": "Di leseng tse pedi, di hole hammoho, ho be ho fihle kotulo; ke hona mohla ho kotulwang ke tla re ho bakotudi: Helang mofoka pele, le o tlame mangata, le o tjhese; le mpe le bokelle koro modikong wa ka.",
			"31": "A ba beela setshwantsho se seng, a re: Mmuso wa mahodimo o tshwana le hlaku ya mosetareta, eo motho a e nkileng, a e sunya tshimong ya hae.",
			"32": "Efela e le yona e nyenyane ho dipeo tsohle, empa ha e hodile, e siya meroho e meng, e fetohe sefate, moo dinonyana tsa lehodimo di tlang, mme di aha makaleng a sona.",
			"33": "A ba bolella setshwantsho se seng, a re: Mmuso wa mahodimo o tshwana le ditomoso tseo mosadi a di nkileng, a di kenya phofong e tekanyo tse tharo, hlama e tle e kokomohe kaofela.",
			"34": "Ditaba tseo tsohle Jesu a di bolella bongata ka ditshwantsho, mme o ne a sa bue letho le bona a sa tshwantshe;",
			"35": "hore ho etsahale polelo ya moporofeta, ya itseng: Ke tla ahlamisa molomo wa ka ka ditshwantsho, ke pepese ditaba tse neng di patilwe haesale lefatshe le thewa.",
			"36": "Hobane Jesu a kgutlise bongata, a kena tlung, mme barutuwa ba hae ba tla ho yena, ba re: Re qaqisetse setshwantsho sa mofoka wa tshimo.",
			"37": "A ba araba, a re: Ya jalang peo e ntle ke Mora motho;",
			"38": "tshimo ke lefatshe; peo e ntle ke bana ba mmuso; mofoka ke bana ba e mobe;",
			"39": "sera se o jadileng ke Diabolose; kotulo ke bofelo ba lefatshe; bakotudi bona ke mangeloi.",
			"40": "Jwaloka mofoka ha o kguwa, o tjheswa mollong, ho tla ba jwalo bofelong ba lefatshe lena.",
			"41": "Mora motho o tla roma mangeloi a hae; a tla kgetha mmusong wa hae dikgopiso tsohle le ba etsang bokgopo,",
			"42": "mme a ba lahlele seboping sa mollo, moo dillo di tla ba teng le ditsikitlano tsa meno.",
			"43": "Mohlang oo, ba lokileng ba tla kganya jwaloka letsatsi mmusong wa Ntata bona. Ya nang le ditsebe ho utlwa, a utlwe.",
			"44": "Hape, mmuso wa mahodimo o jwaloka letlotlo le patilweng tshimong, leo eitseng hobane motho a le fumane, a boela a le pata; mme ka baka la ho le thabela, o rekisa ka ntho tsohle tseo a nang le tsona, o ithekela tshimo eo.",
			"45": "Mmuso wa mahodimo hape o jwaloka mohwebi ya batlang diperela tse ntle.",
			"46": "Hobane a fumane perela ya bohlokwa, a ya, a bapatsa ka ntho tsohle tseo a nang le tsona, mme a ithekela yona.",
			"47": "Hapehape, mmuso wa mahodimo o jwaloka letlowa le akgetsweng lewatleng, mme le bokella ntho tsa mefutafuta.",
			"48": "Ha le tletse, ba le hulela lebopong, ba ntoo dula, ba boloka tse ntle dipitseng tsa bona, ba lahla tse mpe.",
			"49": "Ho tla ba jwalo bofelong ba lefatshe: mangeloi a tla tswa, a kgethe ba kgopo ho ba lokileng,",
			"50": "mme a tla ba lahlela seboping sa mollo; ke moo dillo di tla ba teng le ditsikitlano tsa meno.",
			"51": "Jesu a re ho bona: Ana le utlwisisa taba tseo tsohle na? Ba re ho yena: E, Morena.",
			"52": "A re ho bona: Ke moo ke reng: Mongodi e mong le e mong, ya rutilweng tsa mmuso wa mahodimo, o jwaloka motho eo e leng monga ntlo, ya ntshang dintho tse ntjha le dintho tsa kgale letlotlong la hae.",
			"53": "Yare ha Jesu a qetile ditshwantsho tseo, a tloha teng.",
			"54": "Mme hobane a fihle naheng ya habo, a ruta batho sinagogeng ya bona, ba ba ba tsota, ba re: Na enwa o nkile bohlale bo bokalo le mehlolo e mekalo kae?",
			"55": "Na enwa hase mora wa mmetli na? Ha re re, mmae ke ya bitswang Maria? Le banababo na hase Jakobo, le Josefa, le Simone, le Juda na?",
			"56": "Bokgaitsedie le bona na ha ba moo ho rona kaofela ha bona? Na tseo tsohle enwa o di nkile kae na?",
			"57": "Mme ba kgoptjwa ho yena. Empa Jesu a re ho bona: Moporofeta ha a hloke hlompho haese naheng ya habo le tlung ya hae.",
			"58": "Teng, ha a ka a etsa mehlolo e mengata, ka baka la ho se dumele ha bona."
		},
		"14": {
			"1": "Mehleng eo, Heroda moteterareke a utlwa botumo ba Jesu.",
			"2": "Yaba o re ho bahlanka ba hae: Eo ke Johanne Mokolobetsi: o tsohile bafung, mme ke ka baka leo matla ao a sebetsang ka ho yena.",
			"3": "Efela Heroda o ne a ile a tshwara Johanne, a mo tlama, a mo kenya teronkong, ka baka la Herodiase, mosadi wa Filippi moenae.",
			"4": "Hobane Johanne o ne a itse ho yena: Ha ho a lokela hoba o be le yena.",
			"5": "Heroda o ne a rata a ka mmolaya; empa a tshaba batho; hobane ba ne ba re: Ke moporofeta.",
			"6": "Empa moketeng wa tsatsi la tswalo ya Heroda, moradi wa Herodiase a etsa tjhao pontsheng eo bohle, a kgahlisa Heroda.",
			"7": "Yaba o mo hlapanyetsa ka kano, a re, o tla mo nea ntho eo a ka e qelang, leha e ka ba eng le eng.",
			"8": "Morwetsana ha a laetswe ke mmae a re: Nnee hona jwale hlooho ya Johanne Mokolobetsi ka mokeke.",
			"9": "Morena a swaba; empa ka baka la kano ea hae, le ka baka la ba dutseng dijong le yena, a laela hore a e newe.",
			"10": "A romela ho kgaola Johanne hlooho teronkong.",
			"11": "Hlooho ya hae ya tliswa ka mokeke, ya newa morwetsana, mme a e isa ho mmae.",
			"12": "Jwale barutuwa ba Johanne ba tla, ba nka setopo, ba se epela, ba ntoo ya bolella Jesu.",
			"13": "Jesu yare hoba a utlwe taba eo, a tloha moo ka sekepana, a ikela thoko, feelleng. Eitse hobane matshwele a utlwe, a mo latela teng ka maoto, a etswa metseng.",
			"14": "Jesu ha a etswa, a bona bongata bo boholo, a bo utlwela bohloko, a fodisa ba kulang ba bona.",
			"15": "Yare mantsiboya, barutuwa ba hae ba mo atamela, ba re: Mona ke feelleng, le letsatsi le se le ile; o ke o kgutlise bongata, bo ikele metsaneng ho ithekela dijo.",
			"16": "Empa Jesu a re ho bona: Ha ba tshwanela ho tloha; ba fepeng e le lona.",
			"17": "Ba re ho yena: Ha re na letho mona, haese mahobe a mahlano feela le dihlapi tse pedi.",
			"18": "Yaba o re: Di tliseng mona ho nna.",
			"19": "Eitse hobane a laele bongata ho dula hodima jwang, a nka mahobe a mahlano le dihlapi tse pedi, a lelala hodimo, a boka; a ntoo ngwatha mahobe, a a nea barutuwa ba hae, mme barutuwa ba a abela bongata.",
			"20": "Mme ba eja bohle, ba kgora, ha ba ha thonakwa dingwathwana tse setseng, tsa tlatsa diroto tse leshome le metso e mmedi.",
			"21": "Mme ba jeleng e be e le banna ba batlileng ho ba dikete tse hlano, basadi le bana ba sa balwe.",
			"22": "Yaba Jesu o phakisa a kganna barutuwa ba hae, a re, ba kene sekepaneng, ba mo etelle pele mose wane, ha a sa kgutlisa matshwele.",
			"23": "Eitse hobane a kgutlise matshwele, a nyolohela thabeng ho ya rapela kathoko; a phirimelwa a sa le hona teng a nnotshi.",
			"24": "Sekepana sona se ne se se se le hara lewatle, se tsokotswa ke maqhubu a metsi; hobane moya o ne o se hanela.",
			"25": "Eitse ka tebelo ya bone ya bosiu, Jesu a tla ho bona, a tsamaya hodima lewatle.",
			"26": "Barutuwa ha ba mmona a tsamaya hodima lewatle, ba tshoha, ba re: Ke sethotsela; mme ba meketsa ke ho tshaba.",
			"27": "Empa Jesu a phakisa a bua le bona, a re: Betang dipelo, ke nna, se tshabeng.",
			"28": "Petrose a mo araba, a re: Morena, haeba ke wena, laela hore ke tle ho wena hodima metsi.",
			"29": "A re: Tloo! Yaba Petrose o tswa sekepaneng, a tsamaya hodima metsi, a ya ho Jesu.",
			"30": "Empa ha a bona moya o le bohale, a tshoha, mme ha a qala ho teba, a howa, a re: Morena, mpholose!",
			"31": "Jesu a phakisa a otlolla letsoho, a mo tshwara, a re ho yena: Motho ya tumelo e nyenyane, o belaeletseng?",
			"32": "Yare ha ba kene sekepaneng, moya wa kgutsa.",
			"33": "Yaba ba ka sekepaneng ba atamela, ba mo kgumamela, ba re: Efela o Mora Modimo ruri.",
			"34": "Yare ha ba tsheletse mose, ba kena lefatsheng la Genesarethe.",
			"35": "Banna ba moo ha ba tseba hoba o teng, ba romela mathokong wohle a teng; yaba ba tlisa ho yena bohle ba seng monate.",
			"36": "Ba mo rapela hore a ba dumelle, leha e le ho ama qola feela ya kobo ya hae; mme bohle ba e amang ba fola."
		},
		"15": {
			"1": "Jwale, bangodi le bafarisi ba tla ho Jesu, ba etswa Jerusalema, ba re ho yena:",
			"2": "Barutuwa ba hao ba tlolelang mokgwa wa baholo, ha ba eja bohobe, ba sa hatlela?",
			"3": "A araba, a re ho bona: Le lona, le tlolelang molao wa Modimo ka mokgwa oo le o neanang na?",
			"4": "Hobane Modimo o laetse, wa re: Hlonepha ntatao le mmao. Hape ho itswe: Ya rohakang ntatae, leha e le mmae, a bolawe.",
			"5": "Kanthe lona le re: Ya reng ho ntatae, leha e le ho mmae: Sa tsa ka seo nka beng ke o thusa ka sona, ke se ke se abetse Modimo, ha a na molato, leha a sa hlonephe ntatae le mmae.",
			"6": "Le fetotse molao wa Modimo lefeela ka mokgwa oo le o neanang.",
			"7": "Baikaketsi, Esaia o ne a porofete tsa lona hantle, ha a itse:",
			"8": "Setjhaba sena se ntlhonepha ka molomo feela; empa pelo tsa bona li hole le nna.",
			"9": "Ba nkgumamella lefeela, ha ba ruta dithuto tseo e leng melao ya batho.",
			"10": "Yaba Jesu o bitsa bongata, a re ho bona: Mamelang, le utlwisise.",
			"11": "Se kenang ka hanong hase sona se silafatsang motho, empa se tswang hanong ke sona se silafatsang motho.",
			"12": "Jwale, barutuwa ba mo atamela, ba re ho yena: Na o a tseba hobane bafarisi ba ile ba hlonama ha ba utlwa polelo ya hao na?",
			"13": "A araba, a re: Setlama se seng le se seng se sa hlongwang ke Ntate ya mahodimong, se tla fotholwa.",
			"14": "Ba tloheleng; ke difofu tse tsamaisang difofu; sefofu ha se tsamaisa sefofu se seng, di tla wela sekoting tse pedi.",
			"15": "Petrose a bua, a re ho yena: O re qaqisetse setshwantsho seno.",
			"16": "Empa Jesu a re: Na le sa hloka kelello le lona na?",
			"17": "Na ha le utlwisise hobane tsohle tse kenang ka hanong di ya mpeng, di ntoo lahlelwa mohlabeng?",
			"18": "Empa tse tswang hanong di tswa pelong; ke tsona tse silafatsang motho.",
			"19": "Hobane mehopolo e mebe, dipolao, dipoko, mafebe, dikutso, diketselletso, le dinyefolo, di tswa pelong;",
			"20": "ke tsona dintho tse silafatsang motho; empa haele ho ja ka matsoho a sa hatlelwang, ha ho silafatse motho.",
			"21": "Yaba Jesu o tloha moo, a ikela naheng ya Tire le Sidone.",
			"22": "Teng ha hlaha mosadi wa Mokanana, ya tswang mathokong ao, a howa, a re: Nkutlwele bohloko, Morena, mora Davida! Moradi wa ka o tshwenngwa hampe ke motemona.",
			"23": "Empa Jesu a se ke a mo araba letho. Barutuwa ba hae ba atamela, ba mo rapela, ba re: Mo kgutlise, hobane o howa a ntse a re latela.",
			"24": "A mpa a araba, a re: Ha ke a romelwa ho motho, haese ho dinku tsa ntlo ya Iseraele tse timetseng.",
			"25": "Yaba mosadi o mo atamela, a kgumama, a re ho yena: Morena, nthuse!",
			"26": "Jesu a mpa a mo araba, a re: Ha ho hotle ho amoha bana bohobe ho ba akgela dintjanyana.",
			"27": "Mosadi a re: E, Morena; empa le tsona dintjanyana di ye di thole makumane a wang tafoleng ya beng ba tsona.",
			"28": "Jwale Jesu a araba, a re ho yena: Mosadi, tumelo ya hao e kgolo; a ho etswe ho wena kamoo o ratang kateng. Mme moradi wa hae a fola nakong yona eo.",
			"29": "Jesu a tloha teng, a atamela lewatle la Galelea, mme eitse hobane a nyolohele thabeng, a dula teng.",
			"30": "Matshwele a mangata a tla ho yena, a tlisa dihlotsa, le difofu, le dimumu, le dihole, le ba bang ba bangata; ba ba bea maotong a Jesu, mme a ba fodisa.",
			"31": "Yaba bongata boo bo a makala, ha bo bona dimumu di bua, le dihole di ena le mmele o motle, le dihlotsa di tsamaya, le difofu di bona, mme ba rorisa Modimo wa Iseraele.",
			"32": "Yaba Jesu o bitsa barutuwa ba hae, a re: Ke utlwela bongata boo bohloko, hobane matsatsi a se a le mararo ba dutse ho nna, mme ha ba na letho leo ba le jang; ke sitwa ho ba kgutlisa le tlala, esere mohlomong ba ka kgathala tseleng.",
			"33": "Barutuwa ba hae ba re ho yena: Ana moo, feelleng, re ka nka kae mahobe a ka lekanang ho kgorisa bongata bo bokalo?",
			"34": "Jesu a re ho bona: Le na le mahobe a makae? Ba re: A supileng, le dihlapinyana di se kae.",
			"35": "Yaba o laela bongata ho dula fatshe.",
			"36": "Yare hobane a nke mahobe ao a supileng le dihlapi, a a ngwatha, ha a se a lebohile, a nea barutuwa ba hae, mme barutuwa ba abela bongata.",
			"37": "Bohle ba eja, ba kgora; ha ba ha thonakwa dingwathwana tse setseng, tsa tlatsa diroto tse supileng.",
			"38": "Ba jeleng e ne e le banna ba kete tse nne, ha ho sa balwe basadi le bana.",
			"39": "Mme Jesu hobane a kgutlise matshwele, a kena sekepaneng, a ya naheng ya Magdala."
		},
		"16": {
			"1": "Bafarisi le Basadusi ba tla, ba leka Jesu ka ho kopa hore a ba etsetse pontsho e tswang lehodimong.",
			"2": "Empa yena a araba, a re ho bona: Ha ho se ho phirimile, le ye le re: Le tla sa, hobane lehodimo le lefubedu.",
			"3": "Kameso le re: Kajeno le tla duma, hobane lehodimo le bofubedu bo botsho. Baikaketsi, ha le tseba ho lemoha tshobotsi ya lehodimo hantle, le sitwa keng ho lemoha dipontsho tsa mehla ena na?",
			"4": "Moloko ona o lonya, o mofebi, o batla pontsho, o mpa o ke ke wa newa pontsho, haese pontsho ya moporofeta Jonase. Yaba o ba tlohela, ikela.",
			"5": "Barutuwa ba hae ha ba fihla mose wane, ba fumana hobane ba lebetse ho nka mahobe.",
			"6": "Jesu a re ho bona: Iponeleng, le tshabe ditomoso tsa Bafarisi le tsa Basadusi.",
			"7": "Ba hakanya ka ho bona, ba re: Eka ke hobane re sa ka ra nka mahobe.",
			"8": "Empa Jesu a tseba, mme a re ho bona: Batho ba tumelo e nyenyane, na le hakanya kahare ho lona hoba ke rialo, kahobane le sa ka la nka mahobe na?",
			"9": "Ana ha le eso ho utlwisise na? Na le hakilwe ke mahobe a mahlano a mohla banna ba kete tse hlano, le bokae ba diroto tseo le di nkileng?",
			"10": "Le mahobe a supileng a mohla banna ba kete tse nne, le bokae ba diroto tseo le di nkileng?",
			"11": "Ha le utlwisise keng hore ha ke bolele mahobe, ha ke re, le tshabe ditomoso tsa Bafarisi le tsa Basadusi?",
			"12": "Jwale, ba utlwisisa hore ditomoso tseo a itseng ba di tshabe, hase tsa bohobe, empa e le tsa thuto ya Bafarisi le ya Basadusi.",
			"13": "Jesu eitse ha a fihla naheng ya Sesarea wa Filipi, a botsa barutuwa ba hae, a re: Ke le Mora motho, batho ba re, ke mang na?",
			"14": "Ba re: Ba bang ba re, o Johanne Mokolobetsi; ba bang ba re, o Elia; ba bang ba re, o Jeremia, kapa e mong wa baporofeta.",
			"15": "A re ho bona: Lona, le re, ke mang na?",
			"16": "Yaba Simone Petrose o araba, a re: O Kreste, Mora Modimo o phelang.",
			"17": "Jesu a araba, a re ho yena: O lehlohonolo, Simone, mora Jona; hobane hase nama le madi a o kwaholletseng hoo, empa e le Ntate ya mahodimong.",
			"18": "Hape, ke re ho wena, o Petrose, mme kahodima lejwe leo, ke tla haha kereke ya ka, mme dikgoro tsa nqalo ya bafu ha di ka ke tsa e hlola.",
			"19": "Ke tla o nea dinotlolo tsa mmuso wa mahodimo: hohle hoo o tla ho tlama lefatsheng ho tla tlangwa mahodimong, le hohle hoo o tla ho lokolla lefatsheng ho tla lokollwa mahodimong.",
			"20": "Jwale, a laya barutuwa ba hae, a re, ba se ke ba bolella motho hore ke yena Kreste.",
			"21": "Ho tloha mohlang oo, Jesu a qala ho hlalosetsa barutuwa ba hae hore e ka kgona a ye Jerusalema, a utlwiswe bohloko bo boholo ke baholo le baprista ba baholo le bangodi, mme a bolawe, a ntoo tsoha ka letsatsi la boraro.",
			"22": "Empa Petrose a mo nkela thoko, a qala ho mo kgalemela, a re ho yena: Morena, o ikutlwele bohloko; tse jwalo di ke ke tsa o hlahela.",
			"23": "Jesu a reteleha, a re ho Petrose: Tloha ho nna, Satane, o kgopiso ho nna, hobane ha o utlwe tsa Modimo, o utlwa tsa batho feela.",
			"24": "Yaba Jesu o re ho barutuwa ba hae: Ekare ha motho a rata ho ntatela, a ke a itele, a ipelese sefapano sa hae, mme a ntshale morao.",
			"25": "Hobane e mong le e mong ya ratang ho boloka bophelo ba hae, o tla timellwa ke jona; empa e mong le e mong ya timellwang ke bophelo ba hae ka baka la ka, o tla bo fumana.",
			"26": "Hobane leha motho a ka rua lefatshe lohle, ha a timeletswe ke moya wa hae, ekaba o ruileng ka lona na? Kapa motho moya wa hae a ka o reka kang?",
			"27": "Hobane Mora motho o tla tla ka kganya ya Ntatae le mangeloi a hae; mme mohlang oo, o tla nea e mong le e mong ka tekanyo ya diketso tsa hae.",
			"28": "Kannete ke re ho lona: Ba bang ba teng ho ba emeng mona ba ke keng ba utlwa ho shwa, ba eso ho bone Mora motho a etla mmusong wa hae."
		},
		"17": {
			"1": "Matsatsi a tsheletseng ha a fetile, Jesu a nka Petrose, le Jakobo, le Johanne, ngwanabo Jakobo, mme a ya le bona kathoko, thabeng e telele.",
			"2": "A fetoha pontsheng ho bona; sefahleho sa hae sa kganya jwaloka letsatsi, mme diaparo tsa hae tsa eba tshweu jwaloka lesedi,",
			"3": "mme hang, Moshe le Elia ba ba hlahela, ba buisana le yena.",
			"4": "Yaba Petrose o bua, a re ho Jesu: Morena, ho molemo ha re ka dula mona; ha o rata, a re keng re etse metlotlwane e meraro mona: o mong e be wa hao, o mong e be wa Moshe, o mong e be wa Elia.",
			"5": "A re a sa bua, leru le kganyang la hlaha, la ba kgurumetsa, mme ha utlwahala lentswe le tswang lerung, le re: Enwa ke Mora wa ka ya ratwang, eo ke kgahliswang ke yena; mo utlweng!",
			"6": "Barutuwa ha ba utlwa lentswe, ba wa ka difahleho tsa bona, ba tshohile haholo.",
			"7": "Empa Jesu a atamela, a ba ama, a re: Tsohang, le se ke la tshaba.",
			"8": "Ha ba emisa mahlo, ha ba ka ba hlola ba bona motho, haese Jesu a nnotshi.",
			"9": "Yare ha ba theoha thabeng, Jesu a ba laela, a re: Pono eo le se ke la e bolella motho, Mora motho a eso ho tsohe bafung.",
			"10": "Barutuwa ba hae ba mmotsa, ba re: Na ke ho reng, ha bangodi ba re: E ka kgona Elia a tle pele?",
			"11": "Jesu a araba, a re ho bona: Kannete Elia o tshwanetse ho tla, a busetse tsohle tokeng.",
			"12": "Empa ke re ho lona: Elia o se a tlile, mme ha ba ka ba mo tseba; ba mpa ba mo entse hohle hoo ba ho ratileng; Mora motho le yena o tla utlwiswa bohloko jwalo ke bona.",
			"13": "Jwale, barutuwa ba utlwisisa hobane o ba lekanyetsa Johanne Mokolobetsi.",
			"14": "Eitse ha ba etla ho bongata, motho a mo atamela, a mo kgumamela ka mangwele,",
			"15": "a re: Morena, utlwela mora ka bohloko; hobane o jewa ke sethwathwa, mme o kula hampe; hobane hangata o wela mollong, mme hangata a wele metsing.",
			"16": "Ke se ke mo hlahisitse ho barutuwa ba hao, empa ha ba ka ba tseba ho mo fodisa.",
			"17": "Jesu a araba, a re: Moloko o sa dumeleng, o kgopo, ke tla ba le lona ho le hokae na? Ke tla le behela pelo ho le hokae na? Mo tliseng mona ho nna.",
			"18": "Mme Jesu a kgalemela motemona haholo; motemona a tswa ngwaneng eo, mme ngwana a fola ka yona nako eo.",
			"19": "Yaba barutuwa ba tla ho Jesu kathoko, ba re ho yena: Na re kile ra sitwa keng ho leleka motemona eo?",
			"20": "Jesu a re ho bona: Ke ka baka la ho se dumele ha lona; hobane kannete ke re ho lona: Hoja le na le tumelo e ka ka hlaku ya mosetareta, leha le be le ka re ho thaba ena: Fallela mane; e ka fallela teng, mme ho ka be ho se letho le le sitang.",
			"21": "Empa mofuta oo wa batemona ha o tswe, haese ka thapelo le ka ho itima dijo.",
			"22": "Ha ba sa ya le Galelea, Jesu a re ho bona: Mora motho o tla neelwa matsohong a batho.",
			"23": "Ba tla mmolaya, empa o tla tsoha ka tsatsi la boraro. Yaba barutuwa ba swaba haholo.",
			"24": "Eitse ha ba fihla Kapernauma, ha tla ho Petrose ba bokellang didrakema, ba re ho yena: Ana moruti wa lona ha a ntshe didrakema na?",
			"25": "A re: Tjhe, o a e ntsha. Eitse hobane a kene tlung, Jesu a mo qala, a re ho yena: Ho jwang ho wena, Simone? Marena a lefatshe a nka lekgetho la kgwebo le la lehae ho bafe? Na a ke ho bana ba wona, kapa a ke ho ba ditjhaba tse ding na?",
			"26": "Petrose a re ho yena: Ke ho ba ditjhaba tse ding. Jesu a re ho yena: Ha ho le jwalo bana bona ba lokolohile lekgethong.",
			"27": "Empa ka ho re, re se ke ra ba kgopisa, eya lewatleng, o akgele selope teng, mme o tshwase hlapi e tla phahama pele; etlare ha o e ahlamisa molomo, o tla fumana tjhelete ya setatera teng; o e nke, o ba nee yona, e lefelle nna le wena."
		},
		"18": {
			"1": "Nakong eo, barutuwa ba tla ho Jesu, ba re: E moholo mmusong wa mahodimo ke mang na?",
			"2": "Jesu a bitsa ngwana, a mmea hara bona,",
			"3": "a re: Kannete ke re ho lona: Ha le sa fetohe, la ba jwaloka bana, ha le ka ke la kena mmusong wa mahodimo.",
			"4": "Ka baka leo, e mong le e mong ya ikokobetsang jwaloka ngwana enwa, ke yena eo e leng e moholo mmusong wa mahodimo;",
			"5": "mme e mong le e mong ya amohelang ngwana ya jwaloka enwa ka lebitso la ka, o amohela nna.",
			"6": "Empa e mong le e mong ya kgopisang e mong wa ba banyenyane bana ba dumelang ho nna, ho ka ba molemo ho yena hore a tlamellwe lelwala le leholo molaleng, mme a dihelwe bodibeng ba lewatle.",
			"7": "Ho madimabe lefatshe ka baka la dikgopiso; hobane dikgopiso di tshwanetse ho ba teng; ho mpa ho madimabe motho eo kgopiso e hlahang ka yena.",
			"8": "Empa letsoho la hao, kapa leoto, ha le o dihela, o le pome, o le lahlele hole le wena; hobane ho molemo ho wena ho kena bophelong leha o le tsohane, kapa o le sehlotsa, ho ena le ho lahlelwa mollong o sa feleng, o ena le maoto a mabedi, kapa matsoho a mabedi.",
			"9": "Ha leihlo la hao le o dihela, o le honye, o le lahlele hole le wena. Ho molemo ho wena ho kena bophelong o le moihlwe, ho ena le ho lahlelwa mollong wa dihele, o ena le mahlo a mabedi.",
			"10": "Iponeleng, le se ke la nyedisa le a mong wa ba banyenyane bana; hobane ke re ho lona: Mahodimong, mangeloi a bona a tadima kamehla sefahleho sa Ntate ya mahodimong.",
			"11": "Hobane Mora motho o tlile ho pholosa ho timetseng.",
			"12": "Ana taba ena e jwang ho lona? Ha motho a ena le dinku tse lekgolo, mme e nngwe ho tsona ha e lahlehile, ha ke re, o tla tlohela tse mashome a robileng mono o le mong a metso e robileng mono o le mong dithabeng, a ye ho batla e lahlehileng na?",
			"13": "Mme ha a e fumana, kannete ke re ho lona, o ya e thabela ho fetisa tse mashome a robileng mono o le mong a metso e robileng mono o le mong tse sa lahlehang.",
			"14": "Ka mokgwa o jwalo; thato ya Ntata lona ya mahodimong, ke hore ho se ke ha timela le a mong wa ba banyenyane bana.",
			"15": "Ekare ha ngwaneno a o sitetswe, eya, o mo laye o ena le yena a nnotshi; ha a o mamela, etlaba o ruile ngwaneno.",
			"16": "Empa ha a sa o mamele, o ye le e mong, leha ba ka ba babedi, polelo tsohle di tle di tiye ke melomo ya dipaki tse pedi kapa tse tharo.",
			"17": "Ekare ha a sa ba utlwe, o bolelle kereke; ha a sa utlwe le kereke, ho wena a be jwaloka mohetene le molekgetho.",
			"18": "Kannete ke re ho lona: Tsohle tseo le di tlamang lefatsheng, di tla tlangwa lehodimong; le tsohle tseo le di lokollang lefatsheng, di tla lokollwa lehodimong.",
			"19": "Hape, ke re ho lona: Ba babedi ho lona ha ba dumellane lefatsheng, ntho tsohle tseo ba di kopang, ba tla di newa ke Ntate ya mahodimong.",
			"20": "Hobane moo ba babedi, kapa ba bararo, ba phuthehileng teng ka lebitso la ka, ke teng hara bona.",
			"21": "Yaba hona Petrose a mo atamelang, a re: Morena, ngwaneso ha a ntse a ntshitelwa, ke tla mo tshwarela hakae na? E tla ba hasupa na?",
			"22": "Jesu a re ho yena: Ke re ho wena: E se ke ya eba hasupa, e mpe e be ha mashome a supileng a balwang hasupa.",
			"23": "Ka baka leo, mmuso wa mahodimo o tshwana le morena ya ratileng ho hlahloba bahlanka ba hae.",
			"24": "Eitse ha a qala ho bala, ha tliswa ho yena e mong ya nang le molato wa ditalenta tse dikete tse leshome;",
			"25": "mme kahobane a hlokile seo a ka lefang ka sona, morena wa hae a laela, a re, ho rekiswe ka yena, le ka mosadi wa hae, le ka bana, le ka tsohle tseo a nang le tsona, molato o tle o phethwe jwalo.",
			"26": "Empa mohlanka eo a kgumama pela hae, a mo rapela, a re: Morena, o ke o nthekolohele, mme ke tla o lefela hohle.",
			"27": "Morena wa mohlanka eo a mo utlwela bohloko, a mo lesa, a mo leballa molato.",
			"28": "Empa mohlanka eo, ha a tswela kantle, a teana le molekane wa hae wa bohlanka, ya nang le molato ho yena wa didenare tse lekgolo; yaba o a mo tshwara, a mo kgama, a re: Lefa molato wa hao.",
			"29": "Molekane wa hae wa bohlanka a itihela maotong a hae, a mo rapela, a re: Nthekolohele, mme ke tla o lefela.",
			"30": "Eo a hana, a mpa a ya, a mo kgarameletsa teronkong, hore a be a lefe molato.",
			"31": "Balekane ba hae ba bohlanka, ha ba bona ho entsweng, ba swaba haholo, mme ba ya bolella morena wa bona tsohle tse etsahetseng.",
			"32": "Yaba morena wa hae o mo laetsa, a re ho yena: Mohlanka ya lonya, ke ne ke o leseletse molato wohle wa hao, kahobane o ne o nthapetse.",
			"33": "Ana e ne e se tshwanelo ya hao le wena ho hauhela molekane wa hao wa bohlanka jwalokaha ke ne ke o hauhetse le wena na?",
			"34": "Morena wa hae a ba bohale, mme a mo neela bahlokofatsi, hore a be a lefe molato wohle wa hae.",
			"35": "Le tla etswa jwalo ke Ntate ya mahodimong, ha e mong le e mong wa lona a sa tshwarele ngwanabo ditlolo ka pelo ya hae yohle."
		},
		"19": {
			"1": "Eitse hobane Jesu a qete dipolelo tseo, a tloha Galelea, a ya naheng ya Judea, mose wane wa Jordane.",
			"2": "Matshwele a maholo a mo latela, mme a ba fodisa teng.",
			"3": "Bafarisi le bona ba tla ho yena, ho mo leka; yaba ba re ho yena: Hleka motho o dumeletswe ho hlala mosadi wa hae ka baka la eng le eng na?",
			"4": "A ba araba, a re: Na ha le a ka la bala hobane ya ba entseng tshimolohong, o ba entse e le e motona le e motshehadi,",
			"5": "a re: Ka baka leo, monna o tla tlohela ntatae le mmae, a kgomarele mosadi wa hae, mme ba babedi e tla ba nama e le nngwe feela?",
			"6": "Ha ho le jwalo, ha ba sa hlola ba eba babedi, empa e le nama e le nngwe feela. Ke moo ke reng: Seo Modimo o se kopantseng hammoho, se se ke sa arolwa ke motho.",
			"7": "Ba re ho yena: Moshe eena o laetse keng ho nea mosadi lengolo la hlalo, le ho mo hlala?",
			"8": "A re ho bona: Moshe o le dumeletse ho hlala basadi ba lona ka baka la bothata ba dipelo tsa lona; empa tshimolohong, ho ne ho se jwalo.",
			"9": "Mme ke re ho lona: E mong le e mong ya hlalang mosadi wa hae, haese ka baka la bofebe, a nyala e mong, o a feba; le ya nyalang ya hladilweng, le yena o a feba.",
			"10": "Barutuwa ba hae ba re ho yena: Ha taba tsa monna le mosadi di dutse jwalo, ha ho molemo ho nyala.",
			"11": "A re ho bona: Hase bohle ba nang le kutlwisiso ya taba eo, haese ba e neilweng.",
			"12": "Hobane maqhalaha a teng a tswetsweng a le jwalo dimpeng tsa bomma wona; mme maqhalaha a teng a entsweng maqhalaha ke batho, mme maqhalaha a teng hape a iketseng mahqalaha ka baka la mmuso wa mahodimo. Ya ka utlwisisang taba eo, a e utlwisise.",
			"13": "Jwale, ha tliswa ho Jesu bana, hore a ba bee matsoho, a ba rapelle; empa barutuwa ba kgalemela ba ba tlisang.",
			"14": "Jesu a re ho bona: Lesang bana, le se ke la ba thibela ho tla ho nna; hobane mmuso wa mahodimo ke wa ba jwaloka bona.",
			"15": "Mme eitse ha a ba beile matsoho, a tloha teng.",
			"16": "E mong a tla, a re ho yena: Moruti ya molemo, taba e ntle ke efe eo ke tla e etsa hore ke be le bophelo bo sa feleng?",
			"17": "Jesu a re ho yena: Na o mpitsetsang ya molemo? Ya molemo ke Modimo o nnotshi. Empa ha o rata ho kena bophelong, o boloke melao.",
			"18": "A re ho yena: Efe? Jesu a re: E reng: O se ke wa bolaya, o se ke wa feba, o se ke wa utswa, o se ke wa bea bopaki ba bohata,",
			"19": "o hlonephe ntatao le mmao, mme o rate wa heno jwalokaha o ithata.",
			"20": "Mohlankana eo a re ho yena: Ke e bolokile kaofela esale bonyenyaneng ba ka ho fihlela jwale; na ke sa hlokang?",
			"21": "Jesu a re ho yena: Ha o rata ho phetheha, eya, o rekise ka tseo o nang le tsona, o abele bafumanehi, mme o tla ba le letlotlo lehodimong; o ntano tla, o ntatele.",
			"22": "Empa mohlankana, ha a utlwa polelo eo, a ikela a swabile haholo, hobane e ne e le ya nang le maruo a maholo.",
			"23": "Yaba Jesu o bolella barutuwa ba hae, a re: Kannete ke re ho lona: Ho thata ho morui ho kena mmusong wa mahodimo.",
			"24": "Ke pheta ke re ho lona: Ho ka ba bonolo ha kamele e kena ka lesoba la lemao, ho ena le hore morui a kene mmusong wa mahodimo.",
			"25": "Barutuwa ba hae, ha ba utlwa a rialo, ba tsota haholo, ba re: Ha ho le jwalo, ho ka bolokeha mang na?",
			"26": "Jesu a ba tadima, a re ho bona: Ha e le batho, ke ntho e ba sitang, empa Modimo ha o sitwe ke letho.",
			"27": "Jwale, Petrose a bua, a re ho yena: Bona, rona re tlohetse tsohle, mme re o latetse; re tla ba le eng ka hona?",
			"28": "Yaba Jesu o re ho bona: Kannete ke a le bolella, ke re: Ntjhafalong ya tsohle, mohla Mora motho a dulang teroneng ya kganya ya hae, lona ba ntatetseng, le tla dula le lona diteroneng tse leshome le metso e mmedi, le ahlole meloko ya Iseraele e leshome le metso e mmedi.",
			"29": "E mong le e mong ya tlohetseng matlo, leha e le banababo, kapa bokgaitsedie, leha e le ntatae, kapa mmae, kapa bana, leha e le masimo, ka baka la lebitso la ka, o tla newa halekgolo, a je le lefa la bophelo bo sa feleng.",
			"30": "Empa ba bangata ba pele e tla ba ba morao, mme ba morao e tla ba ba pele."
		},
		"20": {
			"1": "Hobane mmuso wa mahodimo o tshwana le mong a ntlo ya tswileng e sa le kameso, ho memela tshimo ya hae ya merara balemi.",
			"2": "Yare ha a dumellane le balemi hore moputso wa tsatsi le le leng e tla ba tjhelete ya denare, a ba romela tshimong ya hae ya merara.",
			"3": "A boela a tswa e batlile ho ba hora ya boraro, a bona ba bang ba eme lepatlelong la motse, ba sa etse letho.",
			"4": "Le ho bona a re: Eyang le lona tshimong ya ka ya merara, mme ke tla le nea se le loketseng; mme ba ya.",
			"5": "A boela a tswa e batlile ho ba hora ya botshelela, hape le ka ya boroba mono o le mong, mme a boela a etsa jwaloka pele.",
			"6": "Ha a etswa e batlile ho ba hora ya leshome le motso o mong, a bona ba bang ba eme ba sa etse letho; a re ho bona: Le emetseng jwalo tsatsi lohle, le sa etse letho?",
			"7": "Ba re ho yena: Ke kahobane re sa ka ra memuwa ke motho. A re ho bona: Le lona, eyang tshimong ya ka ya merara, mme ke tla le nea se le loketseng.",
			"8": "Hoba e be mantisboya, monga tshimo ya merara a re ho mohlahlobi wa ntlo ya hae: Bitsa balemi, o ba nee moputso wa bona; o qale ka ba morao, o qetelle ka ba pele.",
			"9": "Ba memilweng e batlile ho ba hora ya leshome le motso o mong, ha ba etla, e mong le e mong a fumana denare.",
			"10": "Ba pele, ha ba etla, ba hloma ba tla newa ho fetisang; empa le bona ba newa e mong le e mong denare.",
			"11": "Ha ba e nkile, ba kganana ho monga ntlo,",
			"12": "ba re: Bao ba morao ba sebeditse hora e le nngwe feela, mme o ba lekantse le rona, ba jereng boima ba letsatsi le ho tjhesa ha lona!",
			"13": "A araba, a re ho e mong wa bona: Molekana ka, ha ke o je; ana ha o a dumellana le nna ka denare e le nngwe feela na?",
			"14": "Nka ntho ya hao, o ikele; ke rata ho nea enwa wa morao jwalokaha ke o neile.",
			"15": "Kapa ha ho a ntokela ho etsa ka ntho tsa ka kamoo ke ratang kateng, na? Kapa leihlo la hao le mona kahobane ke le molemo na?",
			"16": "Ka mokgwa o jwalo, ba morao e tla ba ba pele, ba pele e tla ba ba morao.",
			"17": "Yare ha Jesu a nyolohela Jerusalema, a sa le tseleng, a nkela barutuwa ba hae ba leshome le metso e mmedi thoko, a re ho bona:",
			"18": "Bonang, re nyolohela Jerusalema, mme Mora motho o tla neelwa baprista ba baholo le bangodi; ba tla mo ahlolela lefu.",
			"19": "Ba tla mo neela baditjhaba, hore ba mo some, le ho mo shapa, le ho mo thakgisa sefapanong; mme o tla tsoha ka tsatsi la boraro.",
			"20": "Yaba ho tla ho yena mma bara ba Sebedea a ena le bara ba hae, mme a kgumama, a kopa ho hong ho yena.",
			"21": "Jesu a re ho yena: O batlang? Eo a mmolella, a re: Ako re, bara bana ba ka ba babedi ba dule mmusong wa hao, e mong letsohong la hao le letona, e mong ho le letshehadi.",
			"22": "Jesu a araba, a re: Ha le tsebe seo le se kopang. — Na le ka tseba ho nwela senwelong seo ke tla nwela ho sona, le ho kolobetswa ka kolobetso eo ke tla kolobetswa ka yona? Ba re ho yena: Re ka tseba.",
			"23": "A re ho bona: Efela le tla nwela senwelong sa ka, mme le tla kolobetswa ka kolobetso eo ke tla kolobetswa ka yona; empa haele ho dula letsohong la ka le letona, kapa ho le letshehadi, ha ke na ho ho nea motho, haese ba ho lokiseditsweng ke Ntate.",
			"24": "Ba leshome ha ba utlwa taba eo, ba hlonamela Jakobo le moenae Johanne.",
			"25": "Jesu, yare ha a ba bileditse ho yena, a re: Le a tseba hoba marena a ditjhaba a di busa, mme baholo ba tsona ba na le puso e thata hodima tsona.",
			"26": "Ho se ke ha eba jwalo ho lona; empa mang le mang ya ratang ho ba moholo hara lona, e ke e be mohlanka wa lona;",
			"27": "mme mang le mang ya ratang ho ba wa pele hara lona, e be mofo wa lona;",
			"28": "jwaloka Mora motho, ya sa kang a tlela ho sebeletswa, empa e le ho sebeletsa le ho neela bophelo ba hae ho ba topollo ya ba bangata.",
			"29": "Eitse ha ba etswa Jeriko, bongata bo boholo ba latela Jesu.",
			"30": "Batho ba babedi ba foufetseng, ba dutseng pela tsela, ha ba utlwa hoba Jesu o a feta, ba howa, ba re: Morena, mora Davida, re utlwele bohloko!",
			"31": "Mme bongata jwa ba kgalemela, jwa re, ba kgutse; empa bona ba eketsa ho howa, ba re: O re utlwele bohloko, Morena, mora Davida!",
			"32": "Yaba Jesu o ema, a ba bitsa, a re: Le rata ha ke le etsetsa eng?",
			"33": "Ba re ho yena: Morena, mahlo a rona a ke a tutubollwe.",
			"34": "Jesu a ba utlwela bohloko, a ama mahlo a bona, mme hang mahlo a bona a boela a bona; mme ba mo latela."
		},
		"21": {
			"1": "Yare ha ba atametse Jerusalema, ba se ba fihlile Bethefage, thabeng ya Mehlwaare, Jesu a roma barutuwa ba le babedi;",
			"2": "a re ho bona: Eyang motsaneng oo le o lebileng; etlare le sa fihla, le tla be le fumane esele e holehilweng, e nang le petsana ya yona; le di lokolle, mme le ntlisetse tsona.",
			"3": "Etlare ha motho a le botsa taba, le tle le re ho yena: Morena o a di hloka; mme o tla phakisa a di romele.",
			"4": "Hoo hohle ho entswe hore ho tle ho etsahale ho boletsweng ke moporofeta ya itseng:",
			"5": "Bolellang moradi wa Sione, le re: Bona, Morena wa hao ke eo ya tlang ho wena, a le bonolo, a kaletse esele, petsana ya esele e lemaletseng joko.",
			"6": "Ebile barutuwa ba ya, ba etsa kamoo ba laetsweng kateng, ke Jesu.",
			"7": "Ba tlisa esele le petsana ya yona, mme ba ala diaparo tsa bona hodima yona, mme Jesu a e kalla.",
			"8": "Bongata bo boholo ba ala diaparo tsa bona tseleng; mme ba bang ba rema makala a difate, ba a ala tseleng.",
			"9": "Mme bongata bo eteletseng pele le bo latelang ba howa, ba re: Hosanna ho mora Davida! Ho bokwe ya tlang ka lebitso la Morena! Hosanna mahodimong a hodimodimo!",
			"10": "Eitse ha a kena Jerusalema, motse wohle wa hwerehana, wa re: Enwa ke mang na?",
			"11": "Matshwele a re: Ke Jesu, moporofeta ya tswang Nasaretha wa Galelea.",
			"12": "Jesu a kena tempeleng ya Modimo; a lelekela ntle bohle ba bapatsang le ba rekang ka tempeleng; a diha ditafole tsa ba ananyang ditjhelete, le ditulo tsa ba bapatsang ka maeba.",
			"13": "A re ho bona: Ho ngodilwe, ho thwe: Ntlo ya ka e tla bitswa ntlo ya thapelo, athe lona, le e entse lehaha la mashodu.",
			"14": "Ha tla ho yena ka tempeleng difofu le dihlotsa, mme a di fodisa.",
			"15": "Empa baprista ba baholo le bangodi, ha ba bona tse makatsang tseo a di etsang, le bana ba howang ka tempeleng, ba reng: Hosanna ho mora Davida! ba hlonama haholo.",
			"16": "Ba re ho yena: Na o utlwa seo bao ba se bolelang na? Jesu a re ho bona: E, na ha le eso ho ka le bala taba e reng: O tumisitse dithoko tsa hao ka melomo ya bana le ya ba anyang, na?",
			"17": "Yaba o a ba tlohela, a tswa motseng, a ya Bethania, mme a robala teng.",
			"18": "Kameso, ha a boela motseng, a lapa.",
			"19": "A bona sefate sa feiye se pela tsela, a ya ho sona, empa a se ke a fumana letho ho sona, haese mahlaku feela; a re ho sona: Ho se ke ha ba ha hlaha tholwana ho wena le ka ho sa feleng. Yaba sefate sa feiye se omella hanghang.",
			"20": "Barutuwa ha ba bona hoo, ba makala, ba re: Ana sefate sa feiye se omeletse jwang hanghang?",
			"21": "Jesu a araba, a re ho bona: Kannete ke a le bolella, ke re: Holane le na le tumelo, le sa belaele, le ka be le etsa, e seng se entsweng ho sefate sa feiye feela, empa leha le be le ka re ho thaba ena: Tloha, o itihele lewatleng, ho ne ho ka etsahala.",
			"22": "Se seng le se seng seo le se kopang ka thapelo, ha le dumela, le tla se newa.",
			"23": "Eitse hobane a fihle tempeleng, baprista ba baholo le baholo ba setjhaba ba tla ho yena, ha a ntse a ruta, ba re: Ntho tseo, o di etsa ka matla afe? Mme matla ao o a neilwe ke mang?",
			"24": "Jesu a ba araba, a re: Ke tla le botsa taba le nna; ekare ha le mpolella yona, le nna ke tla le bolella matla ao ke etsang ntho tseo ka wona.",
			"25": "Ana kolobetso ya Johanne e ne e tswa kae? Na e ne e tswa lehodimong, kapa bathong? Yaba ba eletsana hammoho, ba re: Ha re re: E ne e tswa lehodimong, o tla re: Ha ho le jwalo, ha le a ka la e dumela keng?",
			"26": "Mme ha re ka re: E ne e tswa bathong, re tshaba bongata, hobane bohle ba re: Johanne ke moporofeta.",
			"27": "Yaba ba araba Jesu, ba re: Ha re tsebe. Mme a re ho bona: Le nna, nke ke ka le bolella matla ao ke etsang ntho tseo ka wona.",
			"28": "Empa le reng ka taba ena? Motho o ne a na le bara ba babedi; a ya ho wa pele, a re ho yena: Mora ka, eya, o sebetse kajeno tshimong ya ka ya merara.",
			"29": "Eo a araba, a re: Ke a hana. Empa hamorao, a baka, a ya.",
			"30": "A ya ho wa bobedi, le ho yena a rialo, mme eo a mo araba, a re: Ke a ya, morena. Empa a se ke a ya.",
			"31": "Ana ho bona ba babedi, ke ofe ya entseng thato ya ntatae? Ba re ho yena: Ke wa pele. Jesu a re ho bona: Kannete ke a le bolella, ke re: Balekgetho le basadi ba bafebi ba le raka mmusong wa Modimo.",
			"32": "Hobane Johanne o tlile ho lona ka tsela ya ho loka, mme ha le a ka la mo dumela; empa balekgetho le basadi ba bafebi ba mo dumetse; mme lona, leha le bone hoo, ha le a ka la baka, la mo dumela.",
			"33": "Utlwang setshwantsho se seng: Motho o ne a le teng, e leng monga ntlo, ya hlomileng tshimo ya merara; a e sireletsa ka motero, a tjheka sehatelo teng, a e hahela tora, mme a e laeletsa balemi, a ntoo eta.",
			"34": "Yare ha nako ya ditholwana e atametse, a roma bahlanka ba hae ho balemi ho nka ditholwana tsa hae.",
			"35": "Empa balemi ba tshwara bahlanka ba hae, ba otla e mong, ba bolaya e mong, ba tlepetsa e mong ka majwe.",
			"36": "A boela a roma bahlanka ba bang, ba fetisang ba pele ka bongata, mme le bona ba etswa jwaloka ba pele.",
			"37": "Ho feleng, a romela mora hae ho bona, a re: Ba tla hlompha mora wa ka.",
			"38": "Empa balemi, ha ba bona mora hae, ba bolellana, ba re: Enwa ke mojalefa; tloong, re mmolayeng, re tle re inkele lefa la hae.",
			"39": "Yaba ba mo tshwara, ba mo lahlela kantle ho tshimo ya merara, ba mmolaya.",
			"40": "Ana mohla monga tshimo ya merara a fihlang, balemi bao o tla ba etsa jwang na?",
			"41": "Ba re ho yena: O tla timetsa ba babe bao hampe, mme tshimo ya hae ya merara o tla e laeletsa balemi basele, ba tla mo nea ditholwana nakong ya tsona.",
			"42": "Jesu a re ho bona: Ana ha le a ka la bala Mangolong taba e reng: Lejwe leo bahahi ba le lahlileng, ke lona le entsweng la motheo, la sekgutlo? Le entswe le jwalo ke Morena, mme ke le makatsang mahlong a rona.",
			"43": "Ka baka leo, ke re ho lona: Mmuso wa Modimo le tla o amohuwa, mme o tla newa setjhaba se seng se tla beha ditholwana tsa wona.",
			"44": "Ya tla wela hodima lejwe leo, o tla robeha, mme le tla pitla eo le mo welang hodimo.",
			"45": "Baprista ba baholo le bafarisi, ha ba utlwile ditshwantsho tseo tsa hae, ba tseba hobane o lekantse bona.",
			"46": "Mme ba batla ho mo tshwara; empa ba tshaba bongata, kahobane ba ne ba re, Jesu ke moporofeta."
		},
		"22": {
			"1": "Jesu a boela a bua le bona ka ditshwantsho, a re:",
			"2": "Mmuso wa mahodimo o tshwana le morena ya neng a etsetse mora wa hae mokete wa bohadi.",
			"3": "A roma bahlanka ba hae ho bitsa ba neng ba memetswe bohadi; empa ba hana ho tla.",
			"4": "A boela a roma bahlanka ba bang, a re: Bolellang ba memilweng, le re: Bonang, ke lokisitse mokete wa ka, dikgomo tsa ka le dinonneng tsa ka di hlabilwe; ntho tsohle di se di lokile; tloong bohading.",
			"5": "Empa bao ba nyedisa, mme ba ya, e mong tshimong ya hae, e mong papatsong ya hae.",
			"6": "Ba bang ba tshwara bahlanka ba hae, ba ba omanya, mme ba ba bolaya.",
			"7": "Morena, ha a utlwa taba tseo, a halefa; a roma makgotla a hae, a timetsa babolai bao, a tjhesa le motse wa bona.",
			"8": "Yaba o bolella bahlanka ba hae, a re: Bohadi bo se bo lokisitswe, empa ba neng ba bo bileditswe, e ne e se ba bo tshwanelang.",
			"9": "Jwale, ikeleng mateonong a ditsela, mme le biletse bohading bohle bao le ba fumanang.",
			"10": "Yaba bahlanka bao ba tswela ditseleng, ba bokella bohle bao ba ba fumanang, dikgwahlapa le dikgabane, mme ntlo ya bohadi ya tlala batho ba dulang dijong.",
			"11": "Eitse morena ha a kena ho bona ba dutseng dijong, a bona motho ya sa aparang kobo ya bohadi.",
			"12": "A re ho yena: Mokana ka, o kene jwang mona, o se na kobo ya bohadi na? Eo a hloka puo.",
			"13": "Yaba morena o re ho bahlanka: Mmofeng maoto le matsoho, le mo nke, mme le mo lahlele lefifing le kantle, moo dillo di tla ba teng le ditsikitlano tsa meno.",
			"14": "Hobane ba bitswang ba bangata, empa ba kgethilweng ha ba bakae.",
			"15": "Yaba bafarisi ba tloha, ba ya rera hammoho kamoo ba ka mo tshwasang ka dipolelo tsa hae kateng.",
			"16": "Ba roma ho yena barutuwa ba bona le Baheroda, ba ya re: Moruti, re a tseba hobane o wa nnete, le hoba o ruta tsela ya Modimo ka nnete, le hoba ha o re letho ka motho; hobane tshobotsi ya motho ha e tadingwe ke wena.",
			"17": "Ka baka leo, o re bolelle kgopolo ya hao: Hleka ho a lokela ho ntshetsa Sesare lekgetho, kapa tjhe?",
			"18": "Empa Jesu a tseba lonya la bona, a re: Le ntekelang, lona baikaketsi?",
			"19": "Mpontsheng tjhelete ya lekgetho. Yaba ba mo hlahisetsa denare.",
			"20": "A re ho bona: Setshwantsho sena ke sa mang, le lengolo lena ke la mang?",
			"21": "Ba re ho yena: Ke la Sesare. Yaba o re: Hommeng! Ntshetsang Sesare tseo e leng tsa Sesare, le ntshetse Modimo tseo e leng tsa Modimo.",
			"22": "Yare ha ba utlwa taba eo, ba makala, ba mo tlohela, ba ikela.",
			"23": "Tsatsing lona leo, basadusi, e leng ba latolang tsoho ya bafu, ba tla ho yena, ba mmotsa,",
			"24": "ba re: Moruti, Moshe o itse: Monna ha a eshwa, a se na bana, ngwanabo o tla nka mosadi wa hae, a hlahisetse ngwanabo bana.",
			"25": "Bara ba motho a le mong, ba supileng, ba ne ba le teng hara rona; e moholo a shwa, a se a nyetse; mme kahobane a sa ka a ba le bana, a siela moenae mosadi wa hae.",
			"26": "Ha eba jwalo le wa bobedi, le wa boraro, ho isa ho wa bosupa.",
			"27": "Kamora bona bohle, mosadi le yena a shwa.",
			"28": "Ana ha ho le jwalo, mohla tsoho ya bafu, mosadi eo e tla ba wa ofe ho ba supileng, haele moo ba kile ba eba le yena bohle?",
			"29": "Empa Jesu a araba, a re ho bona: Le lahlehile ke hobane ha le tsebe Mangolo le matla a Modimo;",
			"30": "hobane tsohong ya bafu, ha ho ka ke ha nyalwa, leha e le ho nyadiswa, empa batho ba tla ba jwaloka mangeloi a Modimo lehodimong.",
			"31": "Haele ka tsoho ya bafu, na ha le a ka la bala hoo le ho boleletsweng ke Modimo, ha o re:",
			"32": "Nna, ke Modimo wa Abrahama, le Modimo wa Isaaka, le Modimo wa Jakobo; Modimo hase Modimo wa bafu, empa ke wa ba phelang.",
			"33": "Mme bongata ha bo utlwa hoo, ba tsota thuto ya hae.",
			"34": "Bafarisi, ha ba utlwa hobane o kgutsisitse basadusi, ba phutheha hammoho.",
			"35": "Yaba e mong wa bona, e leng moruti wa molao, o botsa Jesu, ka ho mo leka, a re:",
			"36": "Moruti, taelo e kgolo ya molao ke efe na?",
			"37": "Jesu a re ho yena: Ke e reng: Rata Morena, Modimo wa hao, ka pelo ya hao yohle, le ka moya wa hao wohle, le ka kgopolo ya hao yohle.",
			"38": "Molao oo ke o moholo wa pele.",
			"39": "Mme wa bobedi, o tshwanang le wona, ke o reng: Rata wa heno jwalokaha o ithata.",
			"40": "Melao e mmedi eo ke motheo wa molao wohle le wa baporofeta.",
			"41": "Bafarisi ha ba phuthehile, Jesu a ba botsa,",
			"42": "a re: Le reng ka Kreste? Ke mora wa mang na? Ba re ho yena: Ke wa Davida.",
			"43": "A re ho bona: Ha ho le jwalo, na Davida ha a bolediswa ke Moya, o mmitsetsang morena wa hae, ha a re:",
			"44": "Morena o itse ho morena ka: Dula letsohong la ka le letona, ke be ke bee bao e leng dira ho wena bonamelo ba maoto a hao?",
			"45": "Na Davida ha a mmitsa morena, e ka ba mora wa hae jwang?",
			"46": "Ha ho a ka ha eba motho ya tsebileng ho mo araba letho, mme haesale mohlang oo, ha ho a ka ha hlola ho eba ya betileng pelo ho mmotsa."
		},
		"23": {
			"1": "Yaba Jesu o bolella bongata le barutuwa ba hae,",
			"2": "a re: Bangodi le bafarisi ba dutse ditulo tsa Moshe.",
			"3": "Ka baka leo, le boloke, mme le etse tsohle tseo ba le bolellang tsona, le mpe le se ke la etsa ka diketso tsa bona; hobane ba bolela, empa ha ba etse.",
			"4": "Ba bofa diphahlo tse boima, tse imelang, mme ba di jarisa batho mahetleng, athe bona ha ba rate ho di sisinya le ka monwana.",
			"5": "Diketso tsa bona tsohle ba di etsa hore ba tle ba bonwe ke batho; hobane ba rwala meqhaka ya difilaktera tse diphara, mme ba lelefatsa dipodi dikobong tsa bona;",
			"6": "ba rata ditulo tsa kapele meketeng, le ditulo tse kapele masinagogeng,",
			"7": "le ho dumediswa mapatlelong, le ho bitswa ke batho: Rabbi! Rabbi!",
			"8": "Empa lona, le se ke la bitswa Rabbi, hobane moruti wa lona o mong, e leng Kreste, mme lona bohle le bana ba motho a le mong.",
			"9": "Le se ke la bitsa motho lefatsheng ntata lona, hobane o mong feela eo e leng Ntata lona, ke ya leng mahodimong.",
			"10": "Hape, le se ke la bitswa benghadi, hobane monghadi wa lona o mong, e leng Kreste.",
			"11": "Empa e moholo ho lona e tla ba mohlanka wa lona.",
			"12": "E mong le e mong ya iphahamisang, o tla kokobetswa, mme e mong le e mong ya ikokobetsang, o tla phahamiswa.",
			"13": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le kwalla batho mmuso wa mahodimo; athe lona, ha le kene teng, mme ba ratang ho kena ha le ba dumelle ho kena.",
			"14": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le metsa matlo a basadi ba bahlolohadi, ka ho re, le ba etsetsa merapelo e melelele; ka baka leo, le tla ahlolwa ka kahlolo e fetisisang.",
			"15": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le tsamaya le lewatle le lefatshe lohle ho etsa morutuwa a le mong, mme ha a se a entswe, le mo etsa ngwana wa dihele habedi ho fetisa lona.",
			"16": "Ho madimabe lona, batsamaisi ba foufetseng, le reng: Motho ha a anne ka tempele, ha ho nang; empa ha a anne ka kgauta ya tempele, o tlamilwe.",
			"17": "Mawatla le difofu! ho hoholo keng? Na ke kgauta, kapa a ke tempele e halaletsang kgauta na?",
			"18": "Hape le re: Motho ha a anne ka aletare, ha ho nang; empa ha a anne ka kabelo e hodima yona, o tlamilwe.",
			"19": "Mawatla le difofu! ho hoholo keng? Na ke kabelo, kapa ke aletare e halaletsang kabelo na?",
			"20": "Mme jwale, ya anang ka aletare, o ana ka yona le ka tsohle tse hodima yona;",
			"21": "le ya anang ka tempele, o ana ka yona le ka ya ahileng ho yona;",
			"22": "le ya anang ka lehodimo, o ana ka terone ya Modimo le ka ya dutseng hodima yona.",
			"23": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le ntsha karolo ya leshome ya kwena le anetha le kumine, mme le siya hoo e leng boholo ba molao, e leng tokiso, le mohau, le tumelo; anthe le ne le tshwanetse ho etsa ntho tsena, le ntse le sa tlohele ho etsa tseno.",
			"24": "Batsamaisi ba foufetseng! le hlotlha ntsitsinyana, athe le metsa kamele!",
			"25": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le hlatswa mohope le sejana kantle, athe kahare ho tletse boshodu le ho senya.",
			"26": "Mofarisi ya sefofu! hlatswa mohope le sejana kahare ho tsona pele, bokantle ba tsona bo tle bo hlweke le bona.",
			"27": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le jwaloka mabitla a kwetlisitsweng, a nang le ponahalo e ntle kantle, kanthebo, kahare, a tletse masapo a bafu le ho bola hohle.",
			"28": "Le lona, ho batho ba le tadimang kantle, ho jwaloka eka le lokile, athe kahare ho lona le tletse boikaketsi le bokgopo.",
			"29": "Ho madimabe lona, bangodi le bafarisi, baikaketsi! hobane le haha mabitla a baporofeta, mme le kgabisa mabitla a ba lokileng;",
			"30": "mme le re: Holane re na be re le teng mehleng ya bontata rona moholo, re ka be re sa ka ra kopana le bona polaong ya baporofeta.",
			"31": "Le ikahlola jwalo hobane le le bana ba ba bolaileng baporofeta,",
			"32": "mme le phethisisa tekanyo ya bontata lona.",
			"33": "Dinoha, madinyane a marabe! Le ka phema kahlolo ya dihele jwang?",
			"34": "Ka baka leo, bonang, ke romela ho lona baporofeta, le ba bohlale, le bangodi; mme le tla bolaya ba bang, le thakgise ba bang difapanong, le shape ba bang masinagogeng a lona, le ba leleke motseng o mong le o mong;",
			"35": "madi wohle a lokileng, a tsholletsweng lefatsheng, a tle a bokane ho lona, ho qala ka madi a Abele ya lokileng, ho tlisa mading a Sakaria, mora Barakia, eo le mmolaileng mahareng a tempele le aletare.",
			"36": "Kannete ke re ho lona: Tseo tsohle di tla hlahela moloko wona!",
			"37": "Jerusalema, Jerusalema, ya bolayang baporofeta, ya tlepetsang ka majwe ba rongwang ho wena, ke ratile hangata hakakang ho bokella bana ba hao, jwaloka kgoho ha e bokella ditsuanyana tsa yona tlasa mapheo a yona, empa le hanne.",
			"38": "Bonang, ntlo ya lona e tla sala e le lesupi,",
			"39": "hobane ke re ho lona: Ho qala jwale, ha le ka ke la hlola le mpona, ho be ho fihle mohla le tla re: Ho bokwe ya tlang ka lebitso la Morena!"
		},
		"24": {
			"1": "Yare Jesu ha a etswa tempeleng, a itsamaela. Yaba barutuwa ba hae ba mo atamela, ho mmontsha mehaho ya tempele.",
			"2": "Empa Jesu a re: Le a di bona ntho tseo tsohle? Kannete ke re ho lona: Ha ho ka ke ha leswa teng lejwe hodima lejwe le leng, le sa heletswang.",
			"3": "Eitse hobane a dule thabeng ya Mehlwaare, barutuwa ba hae ba tla ho yena ba nnotshi, ba re ho yena: Re bolelle nako taba tseo di tla etsahala ka yona, le ntho eo e tla ba pontsho ya ho tla ha hao, le ya ho fela ha lefatshe.",
			"4": "Jesu a ba araba, a re: Iponeleng, le tle le se ke la thetswa ke motho.",
			"5": "Hobane ba bangata ba tla tla ka lebitso la ka, ba re: Ke nna Kreste. Mme ba tla thetsa ba bangata.",
			"6": "Le tla utlwa dintwa le medumo ya dintwa; le mpe le bone hore le se ke la tshoha; tseo tsohle e ka kgona di etsahale, empa le moo ha e eso ho be bona bofelo.",
			"7": "Hobane setjhaba se seng se tla tsohela setjhaba se seng matla, mmuso o mong o lwantshe mmuso o mong; ditlala di tla ba teng, le mafu a akaretsang, le ditshisinyeho tsa lefatshe ka nqa tse ngata.",
			"8": "Empa tseo tsohle e tla ba qalo feela ya tlokotsi.",
			"9": "Mohlang oo, le tla neelwa ho tshwenngwa, le tla bolawa, mme le tla hlouwa ke ditjhaba tsohle ka baka la lebitso la ka.",
			"10": "Ke moo ba bangata ba tla kgoptjwa, mme ba tla ekana, ba tla hloyana.",
			"11": "Ho tla hlaha baporofeta ba bohata ba bangata, mme ba tla thetsa batho ba bangata.",
			"12": "Mme lerato la ba bangata le tla tapa ka baka la ngatafalo ya bokgopo.",
			"13": "Empa ya tiisetsang ho fihlela qetellong, ke yena ya tla bolokeha.",
			"14": "Evangeli ena ya mmuso e tla bolelwa lefatsheng lohle, e tle e be bopaki ho ditjhaba tsohle; mme e tla ba hona bofelo bo hlahang.",
			"15": "Jwale, etlare ha le bona manyala a bofediso, a boletsweng ke moporofeta Daniele, a eme nqalong e halalelang — ya balang a lemohe! —",
			"16": "mohlang oo, ba leng Judea ba balehele dithabeng;",
			"17": "ya leng hodima ntlo, a se ke a theohela ho nka ntho tlung ya hae;",
			"18": "le ya leng masimong, a se ke a kgutlela hae ho nka kobo ya hae.",
			"19": "Ho tla ba madimabe basadi ba baimana, le ba anyesang mehleng eo.",
			"20": "Le rapele hore paleho ya lona e se hlahe mariha, leha e le ka Sabatha.",
			"21": "Hobane mohlang oo, mahlomola a tla ba maholo, haesale le tshimolohong ya lefatshe, ho fihlela jwale, tsietsi e kalo ha e eso ho ka e eba teng, mme ha e ka ke ya boela ya eba teng le ka mohla o le mong.",
			"22": "Le hojane matsatsi ao a se ke a kgutshofatswa palo, ho ne ho ke ke ha phonyoha motho; empa ka baka la bakgethwa, matsatsi ao a tla kgutshofatswa.",
			"23": "Mohlang oo, ha motho a ka re ho lona: Bonang, Kreste o mona, kapa Kreste o mane, le se ke la dumela.",
			"24": "Hobane ho tla hlaha bokreste ba bohata, le baporofeta ba bohata, mme ba tla etsa mehlolo e meholo le meeka e ka thetsang le bona bakgethwa, hola ke ntho e ka etswang.",
			"25": "Bonang, ke le boleletse pele, ditaba di eso ho hlahe.",
			"26": "Ka baka leo, ha ba re ho lona: Bonang, o nahathothe, le se ke la tswela teng; kapa ha ba re: Bonang, o phaposing ya ntlo, le se ke la dumela.",
			"27": "Hobane jwalokaha lehadima le hadima le etswa botjhabela, mme le benyetsa bophirimela, ho tla ha Mora motho ho tla ba jwalo.",
			"28": "Hobane moo setopo se leng teng, ke moo manong a tla bokana teng.",
			"29": "Hanghang kamora mahlomola a mehla eo, letsatsi le tla fifala, kgwedi e tla hloka lesedi, dinaledi di tla wa lehodimong, mme tse matla tsa lehodimo di tla sisinyeha.",
			"30": "Mohlang oo, pontsho ya Mora motho e tla hlaha lehodimong; ke moo ditjhaba tsohle tsa lefatshe di tla iteta difuba, mme di tla bona Mora motho a etla ka maru a lehodimo, ka matla a maholo le ka kganya e kholo.",
			"31": "O tla roma mangeloi a hae a letsang diterompeta tse modumo o moholo, hore a bokelle bakgethwa ba hae meyeng e mene, ho qala pheletsong e nngwe ya mahodimo ho isa ho e nngwe.",
			"32": "Ithuteng ho lekanya ditaba ka sefate sa feiye. Lekala la sona ha le se le le bonolo, mme le se le hlahisa mahlaku, le tseba kahoo hobane lehlabula le haufi.",
			"33": "Le lona, etlare ha le bona tseo tsohle, le tsebe hoba Mora motho o haufi, o monyako.",
			"34": "Kannete ke re ho lona: Moloko wona o ke ke wa feta, tseo tsohle di eso ho etsahale.",
			"35": "Lehodimo le lefatshe di tla feta, empa dipolelo tsa ka di ke ke tsa feta.",
			"36": "Haele letsatsi leo le hora eo, ha ho ya tsebang; esita le mangeloi a lehodimo ha a tsebe, leha e le Mora; empa ho tseba Ntate a nnotshi.",
			"37": "Empa jwalokaha ho bile mehleng ya Nowe, le ho tleng ha Mora motho ho tla ba jwalo.",
			"38": "Hobane jwaloka mehleng e bileng teng pele ho morwallo wa metsi, ha batho ba ne ba eja, ba enwa, ba nyala, ba nyadisa, ha ba ha fihla tsatsi leo Nowe a keneng arekeng ka lona;",
			"39": "ha ba ka ba lemoha, ha ba ha fihla morwallo, mme ba nkwa bohle ke wona: ho tla ba jwalo le ho tleng ha Mora motho.",
			"40": "Mohlang oo, ho banna ba babedi ba tla beng ba le tshimong, e mong o tla nkwa, e mong a siuwe;",
			"41": "ho basadi ba babedi ba tla beng ba sila lelwaleng, e mong o tla nkwa, e mong a siuwe.",
			"42": "Lebelang hle; hobane ha le tsebe nako eo Morena wa lona a tla tla ka yona.",
			"43": "Le mpe le tsebe hobane monga ntlo, hola a ka tseba nako eo senokwane se tla tla ka yona, a ka lebela, mme a ke ke a dumela hoba ntlo ya hae e phunngwe.",
			"44": "Le lona, itokiseng ke hona; hobane Mora motho o tla tla ka nako eo le sa e hopolang.",
			"45": "Ana ke mang mohlanka ya kgabane, ya bohlale, eo morena wa hae a mmeileng moalosi wa bahlanka ba hae ho ba nea dijo nakong ya teng?",
			"46": "Ho lehlohonolo mohlanka eo morena wa hae, mohla a fihlang, a tla mo fumana a etsa jwalo.",
			"47": "Kannete ke re ho lona: O tla mmea moalosi wa mesebetsi ya hae kaofela.",
			"48": "Empa ekare ha mohlanka eo a le kgopo, mme a ipolella pelong, a re: Morena ka o diehile ho fihla;",
			"49": "mme a qala ho otla balekane ba hae ba bohlanka, a eja, a enwa le matahwa;",
			"50": "morena wa mohlanka eo o tla fihla ka letsatsi leo a sa mo lebellang, le ka nako eo a sa e tsebeng.",
			"51": "O tla mo pomahanya dikoto, a mo nee kabelo e le nngwe le baikaketsi; ke moo dillo di tla ba teng le ditsikitlano tsa meno."
		},
		"25": {
			"1": "Mohlang oo, mmuso wa lehodimo o tla tshwana le barwetsana ba leshome ba nkileng dilampi tsa bona, ba tswile ho kgahlanyetsa monyadi.",
			"2": "Ba bahlano ho bona e be e le ba bohlale, ba bahlano ba bang e be e le mawatla.",
			"3": "Mawatla ha a nka dilampi tsa wona, ha a ka a di nkela ole.",
			"4": "Empa ba bohlale ba nka ole dipitseng tsa bona, hammoho le dilampi tsa bona.",
			"5": "Jwale kahobane monyadi a diehile, ba otsela bohle, ba robala.",
			"6": "Yare bosiu bo boholo, ha etswa mohoo, ha thwe: Bonang, monyadi ke eo; tswang, le mo kgahlanyetse.",
			"7": "Yaba barwetsana bao kaofela ba tsoha, ba lokisa dilampi tsa bona.",
			"8": "Mawatla a re ho ba bohlale: Le re qhatsetse ole, hobane dilampi tsa rona di a tima.",
			"9": "Empa ba bohlale ba araba, ba re: Tjhe; e ke ke ya lekana rona le lona; eyang ho bao e rekwang ho bona, le ithekele.",
			"10": "Ba re ba sa ya ithekela, monyadi a fihla, mme ba itokisitseng ba kena le yena bohading, mme ha kwalwa monyako.",
			"11": "Barwetsana ba bang ba ntoo fihla, ba re: Morena, Morena, re bulele!",
			"12": "Empa a ba araba, a re: Kannete ke re ho lona: Ha ke le tsebe.",
			"13": "Lebelang ke hona; kahobane ha le tsebe letsatsi le hora eo Mora motho a tla tla ka yona.",
			"14": "Hobane ho jwaloka motho ya etang, eo eitseng hobane a bitse bahlanka ba hae, a ba neela letlotlo la hae.",
			"15": "A nea e mong ditalenta tse hlano, e mong a mo nea di le pedi, e mong a newa e le nngwe; e mong le e mong a newa ka matla a hae; yaba o a tsamaya.",
			"16": "Ya nkileng ditalenta tse hlano a phakisa a ya, a hweba ka tsona, a rua ditalenta tse ding tse hlano.",
			"17": "Ya nkileng ditalenta tse pedi, a rua tse ding tse pedi le yena ka mokgwa o jwalo.",
			"18": "Empa ya nkileng talenta e le nngwe, a ya, a tjheba fatshe, mme a epela tjhelete ya morena wa hae.",
			"19": "Tshiu tse ngata ha di fetile, morena wa bahlanka bao a fihla, mme a hlahloba ditaba le bona.",
			"20": "Yaba ya nkileng ditalenta tse hlano o tla, a hlahisa tse ding tse hlano, a re: Morena, o ne o nneile ditalenta tse hlano; bona, ke ruile tse ding tse hlano kahodima tsona.",
			"21": "Morena wa hae a re ho yena: Ke hantle, mohlanka ya molemo, ya kgabane; o bile kgabane dinthong di se kae, jwale ke tla o bea hodima tse ngata; kena thabong ya morena wa hao.",
			"22": "Ha tla le ya neilweng ditalenta tse pedi, a re: Morena, o ne o nneile ditalenta tse pedi; bona, ke ruile tse ding tse pedi kahodima tsona.",
			"23": "Morena wa hae a re ho yena: Ke hantle, mohlanka ya molemo, ya kgabane; o bile kgabane dinthong di se kae, jwale ke tla o bea hodima tse ngata; kena thabong ya morena wa hao.",
			"24": "Ya neilweng talenta e le nngwe le yena a tla, a re: Morena, ke o tsebile hobane o motho ya thata, ya kotulang moo o sa kang wa jala teng, mme o bokella moo o sa kang wa hasa peo teng.",
			"25": "Ke tshabile ke hona, mme ke ile ka pata talenta ya hao mobung; bona, e sa le ena ntho ya hao.",
			"26": "Yaba morena wa hae o mo araba, a re: Mohlanka ya lonya, ya sebodu, o ne o tseba hobane ke kotula moo ke sa kang ka jala teng, mme ke bokella moo ke sa kang ka hasa peo teng.",
			"27": "Ha ho le jwalo, o ka be o beile tjhelete ya ka bankeng, mme ha ke fihla, nka be ke latile ntho ya ka le phaello ya yona.",
			"28": "Mo amoheng talenta hle, le e nee ya nang le ditalenta tse leshome.",
			"29": "Hobane e mong le e mong ya nang le ntho o tla newa, mme ho tla tlala ho yena; empa ya se nang letho, o tla amohuwa le seo a nang le sona.",
			"30": "Lahlelang mohlanka ya sebodu mafifing a kantle, moo dillo di tla ba teng le ditsikitlano tsa meno.",
			"31": "Mohla Mora motho a tlang a teetswe hare ke kganya ya hae, mangeloi a ena le yena kaofela, ke moo a tla dula teroneng ya kganya ya hae.",
			"32": "Ditjhaba tsohle di tla bokellwa pela hae; mme o tla kgetha ba bang ho ba bang, jwaloka modisa ha a kgetha dinku ho dipodi.",
			"33": "O tla bea dinku ka letsohong la hae le letona, a bee dipodi ho le letshehadi.",
			"34": "Ke moo Morena a tla bolella ba ka letsohong la hae le letona, a re: Tloong, lona ba hlohonolofaditsweng ke Ntate, le amohele mmuso oo le o lokiseditsweng haesale lefatshe le thewa.",
			"35": "Hobane ke na be ke lapile, mme le kile la nnea ka ja; ke na be ke nyorilwe, mme le kile la nnea ka nwa; ke na be ke le moeti, mme la nkenya tlung ha lona;",
			"36": "ke na be ke hlobotse, mme la nkapesa; ke na be ke baba, mme le tlile ho mpona; ke na be ke le teronkong, mme la tla ho nna.",
			"37": "Etlaba hona ba lokileng ba mo arabang, ba re: Morena, ke neneng re kileng ra o bona o lapile, mme ra o nea wa ja; leha o nyorilwe, mme ra o nea wa nwa?",
			"38": "Ke neng re o boneng o le moeti, mme re kileng ra o kenya tlung ha rona; leha o hlobotse, mme ra o apesa?",
			"39": "Ke neng re o boneng o baba, mohlomong o le teronkong, mme ra ya ho wena?",
			"40": "Morena o tla ba araba, a re: Kannete ke a le bolella, ke re: Tseo le di entseng e monyenyane ho baena ba ka, le di entse nna.",
			"41": "Jwale, o tla re ho ba ka letsohong la hae le letshehadi: Tlohang ho nna, lona ba rohakilweng, le ye mollong o sa feleng, o lokiseditsweng Diabolose le mangeloi a hae.",
			"42": "Hobane ke na be ke lapile, mme ha le a ka la nnea ka ja; ke na be ke nyorilwe, mme ha le a ka la nnea ka nwa;",
			"43": "ke na be ke le moeti, mme ha le a ka la nkenya tlung ha lona; ke na be ke hlobotse, mme ha le a ka la nkapesa; ke na be ke le pabong, le teronkong, mme ha le a ka la tla mpona.",
			"44": "Ke moo le bona ba tla mo araba, ba re: Morena, ke neng re kileng ra o bona o lapile, leha o nyorilwe, leha o le moeti, leha o hlobotse, leha o baba, leha o le teronkong, mme re sa ka ra o thusa?",
			"45": "Ke moo a tla ba araba, a re: Kannete ke re ho lona: Erekaha le sa ka la etsa jwalo ho e mong wa ba banyenyane bana, ha le a ka la nketsa jwalo le nna.",
			"46": "Mme bona bao ba tla ya bohlokong bo sa feleng, empa ba lokileng ba tla ya bophelong bo sa feleng."
		},
		"26": {
			"1": "Eitse hobane Jesu a qete dipolelo tseo tsohle, a bolella barutuwa ba hae, a re:",
			"2": "Le a tseba hobane matsatsi a sa tla ba mabedi, e ntoo ba Paseka; mme Mora motho o tla neelwa ho thakgiswa sefapanong.",
			"3": "Mohlang oo, baprista ba baholo, le bangodi, le baholo ba setjhaba, ba phuthehela lebaleng la moprista e moholo, ya bitswang Kaiafa.",
			"4": "Ba etsa morero wa ho tshwara Jesu ka bohlale, ba tle ba mmolaye.",
			"5": "Empa ba re: A e se be mohla mokete, esere mohlomong moferefere wa eba teng hara setjhaba.",
			"6": "Yare ha Jesu a le Bethania, tlung ya Simone, molepera,",
			"7": "ha tla ho yena mosadi ya nang le pitsa ya alebastera, e tletseng setlolo se theko e thata, mme a se tshela hloohong ya Jesu ha a dutse dijong.",
			"8": "Barutuwa ba hae, ha ba bona hoo, ba hlonama, ba re: Tshenyeho eo e thusang?",
			"9": "Athe setlolo seo se ka be se rekile tjhelete tse ngata, tsa newa bafumanehi.",
			"10": "Empa Jesu a lemoha hoo, a re ho bona: Le utlwisetsang mosadi eo bohloko? Athe o nkentse ketso e molemo.",
			"11": "Hobane haele bafumanehi, le tla nne le be le bona kamehla, empa nna, le ke ke la ba le nna kamehla.",
			"12": "Hobane ha a tshela setlolo seo mmeleng wa ka, o etseditse kepelo ya ka.",
			"13": "Kannete ke re ho lona: Hohle lefatsheng, moo Evangeli ena e tla bolelwa teng, se entsweng ke yena se tla bolelwa, a tle a hopolwe ka sona.",
			"14": "Yaba e mong wa ba leshome le metso e mmedi, ya bitswang Judase Iskariota, o ikela ho baprista ba baholo,",
			"15": "a re: Le tla nneang, ke tle ke le neele yena? Mme ba mmalla ditjhelete tsa silefera tsa mashome a mararo.",
			"16": "Yaba moo a qalang ho batla sebaka sa ho ba neela yena.",
			"17": "Ka letsatsi le qalang mokete wa mahobe a se nang ditomoso, barutuwa ba tla ho Jesu, ba re ho yena: O rata ha re o lokisetsa kae Paseka, moo o ka e jelang teng?",
			"18": "A re: Eyang motseng, ho nnyeo, le re ho yena: Moruti o re: Nako ya ka e haufi; ke tla etsa Paseka ha hao le barutuwa ba ka.",
			"19": "Barutuwa ba etsa jwalokaha Jesu a ba laetse, mme ba lokisa Paseka.",
			"20": "Ka nako ya shwalane, Jesu a dula dijong le barutuwa ba leshome le metso e mmedi.",
			"21": "Ba ntse ba eja, a re ho bona: Kannete ke re ho lona: E mong wa lona o tla nkeka.",
			"22": "Yaba ba swaba haholo, mme e mong le e mong a qala ho mmotsa, a re: A ke nna, Morena?",
			"23": "A araba a re: Ya kentseng letsoho morifing le nna, ke yena ya tla nkeka.",
			"24": "Haele Mora motho, o ikela jwalokaha ho ngodilwe bakeng sa hae; empa ho madimabe motho eo Mora motho a ekang ke yena; ho ka be ho le molemo ho motho eo hola a sa ka a tswalwa.",
			"25": "Judase, e leng yena ya mo ekang, a araba, a re: Na ke nna, Rabbi? Jesu a re ho yena: O boletse teng.",
			"26": "Ba re ba sa ja, Jesu a nka bohobe, mme eitse hobane a boke, a bo ngwatha, a bo nea barutuwa ba hae, a re ho bona; Nkang, le je; hona ke mmele wa ka.",
			"27": "A nka le senwelo, a leboha, a ba nea sona, a re: Nwelang ho sona bohle;",
			"28": "hobane hona ke madi a ka a selekane se setjha, a tshollelwang ba bangata tshwarelong ya dibe.",
			"29": "Empa ke re ho lona: Ho qala nakong yona ena, ha ke sa tla hlola ke enwa se behwang ke morara, ho tla be ho fihle tsatsi leo ke tla se nwa le lona se le setjha, mmusong wa Ntate.",
			"30": "Eitse hobane ba bine dithoko, ba ya thabeng ya Mehlwaare.",
			"31": "Yaba Jesu o re ho bona: Bosiung bona le tla kgoptjwa bohle ho nna; hobane ho ngodilwe, ho thwe: Ke tla otla modisa, mme dinku tsa mohlape di tla qhalana.",
			"32": "Empa etlare hobane ke tsohe, ke tla le etella pele ho ya Galelea.",
			"33": "Petrose a bua, a re ho yena: Leha bohle ba ka kgoptjwa ho wena, nna nke ke ka kgoptjwa le hanyenyane.",
			"34": "Jesu a re ho yena: Kannete ke re ho wena: Ka bona bosiu bona, mokoko ha o eso ho lle, o tla be o se o ntatotse hararo.",
			"35": "Petrose a re ho yena: Leha ho ka thwe, ke shwe le wena, nke ke ka ba ka o latola. Le barutuwa bohle ba bolela jwalo.",
			"36": "Jwale, Jesu a ya le bona nqalong e bitswang Gethesemane. A re ho barutuwa ba hae: Dulang mona, ke sa ya mane ho rapela.",
			"37": "A ya le Petrose le bara ba Sebedea ba babedi, mme a qala ho swaba le ho tsieleha haholo.",
			"38": "A re ho bona: Moya wa ka o tshwerwe ka nqa tsohle ke maswabi a isang lefung; salang mona, le fadimehe le nna.",
			"39": "A ntoo ya pejana, a wela fatshe ka sefahleho, a rapela, a re: Ntate, ekare ha ho ka etswa, ako fetisetse senwelo sena hole le nna? Empa ho se ke ha etswa thato ya ka, ho mpe ho etswe ya hao!",
			"40": "Mme ha a etla ho barutuwa ba hae, a ba fumana ba robetse; a re ho Petrose: Le sitilwe jwang ke ho fadimeha le nna hora e le nngwe na?",
			"41": "Fadimehang, le rapele, hore le se kene molekong; hobane moya o mafolofolo, empa nama yona e a fokola.",
			"42": "A boela a ya la bobedi, a rapela, a re: Ntate, ekare ha e se ntho e ka etswang hore senwelo sena se fetele hole le nna ke sa nwele ho sona, a ho etswe thato ya hao!",
			"43": "Ha a kgutlela ho bona, a ba fumana ba boetse ba robetse; hobane mahlo a bona a ne a le boima.",
			"44": "A ba tlohela, a boela a ya, a rapela la boraro, a bua jwalokaha a buile pele.",
			"45": "Yaba o ya ho barutuwa ba hae, a re ho bona: Robalang jwale, le kgatholohe; bonang, nako e atametse, Mora motho o se a ya neelwa matsohong a baetsadibe.",
			"46": "Emang, a re yeng; bonang, ya nkekang o se a le haufi.",
			"47": "A re a sa rialo, ha hlaha Judase, e mong wa ba leshome le metso e mmedi, a ena le sehlopha se seholo, se tshwereng disabole le melamu, se romilweng ke baprista ba baholo le baholo ba setjhaba.",
			"48": "Ya mo ekileng o ne a ba neile pontsho, a re: Eo ke tla mo aka, ke yena; le mo tshware.",
			"49": "A phakisa a atamela Jesu, a re: Dumela Rabbi! Mme a mo aka.",
			"50": "Jesu a re ho yena: O tletseng mona, molekana ka? Jwale, ba atamela, ba isa matsoho ho Jesu, ba mo tshwara.",
			"51": "Lekgatheng leo, e mong wa ba nang le Jesu a isa letsoho saboleng ya hae, a e tsomola, mme a hlatha mohlanka wa moprista e moholo ka yona, a mo kgaola tsebe.",
			"52": "Yaba Jesu o re ho yena: Busetsa sabole ya hao selateng sa yona, hobane bohle ba tshwereng sabole, ba tla bolawa ka sabole.",
			"53": "Kapa na o re, nka sitwa ho rapela Ntate hona jwale, mme o ba a ya nnea dilegione tsa mangeloi tse leshome le metso e mmedi, le tse fetisang?",
			"54": "Empa ha nka etsa jwalo, Mangolo a ka phethahala jwang, a boletseng hore e ka kgona ho be jwalo?",
			"55": "Yaba Jesu o re ho makgotla: Le tswile hae ho tla ntshwara ka disabole le ka melamu, jwalokaha eka ke leshodu! Ke ne ke ntse ke dula le lona tempeleng ka tsatsi le leng le le leng, ke ruta, mme ha le a ka la ntshwara!",
			"56": "Empa hoo hohle ho etsahetse, hore mangolo a baporofeta a phethahale. Jwale, barutuwa bohle ba mo tlohela, ba baleha.",
			"57": "Ba tshwereng Jesu ba mo isa ho Kaiafa, moprista e moholo, moo bangodi le baholo ba neng ba phuthehetse teng.",
			"58": "Petrose a mo latela a le hole, mme a fihla tlung ya moprista e moholo; mme eitse hobane a kene lebaleng, a dula le bahlanka, a tle a bone qetello ya ditaba tseo.",
			"59": "Jwale, baprista ba baholo le baholo le lekgotla lohle ba batla bopaki ba bohata bo ka qosang Jesu, hore ba tle ba mmolaye.",
			"60": "Empa ha ba ka ba bo bona, leha dipaki tsa bohata tse ngata di itlhahisitse. Empa ho feleng ha hlaha dipaki tse pedi tsa bohata,",
			"61": "tsa re: Enwa o kile a re: Ke na le matla a ho qhaqha tempele ya Modimo, ke be ke e tsose ka matsatsi a mararo.",
			"62": "Yaba moprista e moholo o ema, a re ho yena: Na ha o arabe letho na? Seo ba o qosang ka sona keng na?",
			"63": "Empa Jesu a kgutsa. Yaba moprista e moholo o boela a bua, a re ho yena: Ke u antsha ka Modimo o phelang, hore o re bolelle haeba o Kreste, Mora wa Modimo.",
			"64": "Jesu a re ho yena: O boletse teng; hape, ke re ho lona: Ho tloha jwale, le tla bona Mora motho a dutse letsohong le letona la matla a Modimo, mme a etla ka maru a lehodimo.",
			"65": "Jwale, moprista e moholo a hahola diaparo tsa hae, a re: O nyefotse! Re sa batlelang dipaki? Bonang, le utlwile nyefolo ya hae jwale.",
			"66": "Ho jwang ho lona? Ba araba, ba re. O tshwanelwa ke lefu.",
			"67": "Ebile ba mo tshwela ka mathe mahlong, ba mo jabela, ba bang ba mo otla ka dithupa,",
			"68": "ba re: Kreste, o re porofetele: ya o otlileng ke mang?",
			"69": "Petrose yena o ne a dutse kantle lebaleng. Yaba ho fihla ho yena lekgabunyane le leng, la re: Le wena, o ne o ena le Jesu wa Galelea.",
			"70": "Empa yena a latola pontsheng ya bohle, a re: Ha ke tsebe seo o se bolelang.",
			"71": "Eitse ha a tswela kgotlwaneng, lekgabunyane le leng la mmona, la re ho ba leng moo: Le enwa o ne a ena le Jesu wa Nasaretha.",
			"72": "A boela a latola ka ho hlapanya, a re: Motho eo ha ke mo tsebe.",
			"73": "Ho se hokaenyana, ba emeng moo ba atamela, ba re ho Petrose: Le wena, o wa bona ruri; hobane puo ya hao e a o bonahatsa.",
			"74": "Yaba o qala ho hlapanya, a ana, a re: Motho eo ha ke mo tsebe. Athe kapelepele mokoko wa lla.",
			"75": "Yaba Petrose o elellwa se boletsweng ke Jesu, kamoo a neng a itse ho yena kateng: Mokoko ha o eso ho lle, o tla be o se o ntatotse hararo. Yaba o tswela ntle, a lla habohloko."
		},
		"27": {
			"1": "Yare kameso, baprista bohle ba baholo le baholo ba setjhaba ba rera Jesu ho mmolaya.",
			"2": "Eitse hobane ba mo tlame, ba ya le yena, ba mo neela Ponse Pilato, mmusisi.",
			"3": "Jwale, Judase ya mo ekileng, ha a bona hoba Jesu o tswilwe, a inyatsa; a busetsa ditjhelete tsa silefera tse mashome a mararo ho baprista ba baholo le ho baholo;",
			"4": "a re: Ke sitilwe ha ke ekile madi a se nang molato. Ba re: E ka ba eng ho rona? Ho iponele wena teng.",
			"5": "Ebile ke ha a lahlela ditjhelete tsa silefera tempeleng, a tloha, a iphenetha.",
			"6": "Baprista ba baholo ba nka ditjhelete, ba re: Ha ho a tshwanela hore di kenngwe polokelong ya tempele, hobane ke theko ya madi.",
			"7": "Mme hobane ba rere, ba reka tshimo ya mmopi wa dipitsa ka tsona, ho epela baditjhaba teng.",
			"8": "Ka baka leo, tshimo eo e bitsitswe Tshimo ya madi, ho fihlela kajeno.",
			"9": "Ke moo ho etsahetseng ho boletsweng ke moporofeta Jeremia, ha a re: Ba nkile ditjhelete tse mashome a mararo, theko ya ya lekanntsweng, eo bara ba Iseraele ba mo lekantseng jwalo,",
			"10": "ba di ntshitse ho reka tshimo ya mmopi, jwalokaha Morena a ne a ntaetse.",
			"11": "Haele Jesu, a ema pela mmusisi, mme mmusisi a mmotsa, a re: Na o morena wa Bajuda na? Jesu a re ho yena: O boletse teng.",
			"12": "Empa leha a ne a qoswa ke baprista ba baholo le ke baholo, ha a ka a araba letho.",
			"13": "Yaba Pilato o re ho yena: Na ha o utlwe ha ba o qosa hakakale na?",
			"14": "Empa ha a ka a araba lentswe leha le le leng; mmusisi a ba a makala haholo.",
			"15": "E ne e le mokgwa wa mmusisi, ka mokete oo, ho lokollela setjhaba motlamuwa e mong, eo ba mo ratang.",
			"16": "Mohlang oo, ho bo ho le motlamuwa e mong ya tumileng, ya bitswang Barabase.",
			"17": "Ha ba phuthehile, Pilato a re ho bona: Le rata ha ke le lokollela ofe, haele Barabase, kapa Jesu ya bitswang Kreste?",
			"18": "Hobane o ne a tseba hobane ba mo neetse yena ka mona.",
			"19": "Yare ha a sa dutse setulong sa kahlolo, mosadi wa hae a romela ho yena, a re: O se ke wa ba le taba, leha e le e nngwe, le motho eo ya lokileng; hobane ke utlwile bohloko bo boholo ka baka la hae, ha ke lora kajeno.",
			"20": "Empa baprista ba baholo le baholo ba hlohlelletsa bongata hore ba kope Barabase, Jesu yena a bolawe.",
			"21": "Mmusisi a boela a bua, a re ho bona: Le rata ha ke le lokollela ofe ho ba babedi bao? Ba re: Barabase.",
			"22": "Pilato a re ho bona: Mme Jesu, ya bitswang Kreste, yena ke mo etse jwang na? Bohle ba re ho yena: A thakgiswe!",
			"23": "Mmusisi a re: Na o entse bobe bofe? Empa ba totisa ho howa, ba re: A thakgiswe!",
			"24": "Jwale, Pilato ha a bona hobane ha a thuse letho, le hoba moferefere o ntse o tota, a nka metsi, a hatlela pontsheng ho bongata, a re: Ha ke na molato mading a motho eo ya lokileng; ho iponele lona.",
			"25": "Setjhaba kaofela sa araba, sa re: Madi a hae a be hodima rona, le hodima bana ba rona.",
			"26": "Jwale, a ba lokollela Barabase; haele Jesu, eitse hobane a mo shape, a mo neela hore a thakgiswe.",
			"27": "Yaba bahlabani ba mmusisi ba isa Jesu kgotla, ba mmokellela sehlopha sohle sa bona.",
			"28": "Ba mo hlobodisa, ba ntoo mo apesa kobo e kgubedu.",
			"29": "Ba loha mofapahlooho wa meutlwa, ba mo rwesa wona hloohong; ba mo tshwarisa leqala letsohong la hae le letona, mme ba kgumama ka mangwele pela hae, ba mo soma, ba re: Dumela, morena wa Bajuda!",
			"30": "Ba mo tshwela ka mathe, ba nka leqala, ba mmata hloohong.",
			"31": "Eitse hobane ba mo some jwalo, ba mo hlobodisa kobo eo, ba mo apesa diaparo tsa hae, mme ba ya le yena ho mo thakgisa.",
			"32": "Ha ba etswa, ba fumana monna wa Sirene, ya bitswang Simone, ba mo qobella hore a jare sefapano sa Jesu.",
			"33": "Eitse hobane ba fihle thoteng e bitswang Golgotha, e leng ho re, thota ya lehata,",
			"34": "ba mo nea asene e tswakilweng ka nyooko, hore a nwe; empa hobane a e latswe, a hana ho nwa.",
			"35": "Yare hobane ba mo thakgise, ba arolelana diaparo tsa hae, ba di laola ka lotho; hore ho etsahale ho boletsweng ke moporofeta, ha a re: Ba arolelane diaparo tsa ka, mme ba laotse kobo ya ka ka lotho.",
			"36": "Ba ntoo dula, ba mo lebela moo.",
			"37": "Ka hodimo ho hlooho ya hae ba maneha lengolo le bolelang molato wa hae, le reng: Enwa ke Jesu, morena wa Bajuda.",
			"38": "Jwale, ba thakgisa le yena disenyi tse pedi, se seng ka letsohong le letona, se seng ka ho le letshehadi.",
			"39": "Ba fetang ba ne ba mo nyefola, ba mo sisinyetsa dihlooho, ba re:",
			"40": "Wena, ya qhaqhang tempele, o reng o tla e tsosa ka matsatsi a mararo, ipholose he! Ha o le Mora Modimo, theoha sefapanong!",
			"41": "Ka mokgwa o jwalo, le bona baprista ba baholo ba mo soma, ba ena le bangodi le baholo, ba re:",
			"42": "O pholositse ba bang, yena o sitwa ho ipholosa! Haele yena morena wa Iseraele, a ke a theohe jwale sefapanong, mme re tla dumela ho yena.",
			"43": "O tshepile Modimo; a o mo lwele jwale, ha o mo rata; hobane o itse: Ke Mora Modimo.",
			"44": "Disenyi tse thakgisitsweng le yena, le tsona di ne di mo hlapaola jwalo.",
			"45": "Ka hora ya botshelela, lefifi la eba teng lefatsheng lohle, ho fihlela hora ya boroba mono o le mong.",
			"46": "Ha ho batlile ho ba hora ya boroba mono o le mong, Jesu a howa ka lentswe le leholo, a re: Eli, Eli, lamma sabakthani? ke ho re: Modimo wa ka, Modimo wa ka, o ntloheletseng?",
			"47": "Ba bang ho ba leng moo, ha ba utlwa, ba re: O bitsa Elia.",
			"48": "Yaba e mong wa bona o phakisa a matha, a nka seponse, a se tlatsa ka asene, a se hloma leqaleng, a mo nwesa.",
			"49": "Empa ba bang ba re: Lesang, re ke re bone na Elia o tla tla mo pholosa.",
			"50": "Empa Jesu a boela a meketsa ka lentswe le leholo, mme a neela moya.",
			"51": "Mme hang, lesela la tempele la hahoha kalehare, ho qala hodimo ho isa tlase; lefatshe la sisinyeha, le mafika a kgephoha;",
			"52": "mabitla a buleha, le ditopo tse ngata tsa bahalaledi ba robetseng tsa tsoha.",
			"53": "Eitse hobane ba tswe mabitleng, kamora tsoho ya hae, ba kena motseng o halalelang, mme ba ipontsha ho ba bangata.",
			"54": "Molaodi wa lekgolo, le ba nang le yena, ba ntseng ba lebela Jesu, ha ba bona tshisinyeho ya lefatshe le tse hlahileng, ba tshoha haholo, ba re: Eo e ne e le Mora wa Modimo ruri!",
			"55": "Basadi ba bangata ba ne ba le teng moo, ba tadimile ba le hole, e le ba neng ba mo latetse ha a etswa Galelea, ba mo sebeleditseng.",
			"56": "Ho bona ho ne ho le Maria Magdalena, le Maria, mma Jakobo le Jose, le mma bara ba Sebedea.",
			"57": "Yare mantsiboya, ha tla monna ya ruileng wa Arimathea, lebitso la hae e le Josefa; le yena e ne e le morutuwa wa Jesu.",
			"58": "A ya ho Pilato, a kopa setopo sa Jesu. Yaba Pilato o laela hore a se newe.",
			"59": "Eitse hobane a nke setopo, a se phuthela ka lesela le lesweu;",
			"60": "a se bea lebitleng la hae le letjha, leo a le betlileng lefikeng; mme hobane a phikolosetse lejwe le leholo pela monyako wa lebitla, a ikela.",
			"61": "Athe Maria Magdalena o ne a le moo le Maria e mong, ba dutse pela lebitla.",
			"62": "Ka hosasane, e leng tsatsi le hlahlamang la hlekelo, baprista ba baholo le bafarisi ba phuthehela ho Pilato,",
			"63": "ba re: Morena, re sa hopola hobane mothetsi eo, ha a sa phela, o ne a re: Ke tla tsoha ka letsatsi la boraro.",
			"64": "Ka baka leo, ako laele hore lebitla le tiiswe, letsatsi la boraro le be le tjhabe, esere mohlomong barutuwa ba hae ba mo utswa, mme ba re ho setjhaba: O tsohile bafung; leshano leo la ho fela le ne le tla fetisa la pele.",
			"65": "Pilato a re ho bona: Le na le balebedi; eyang, le tiise kamoo le tsebang kateng.",
			"66": "Yaba ba ya, ba tiisa lebitla, ba lebeletsa lejwe ka letshwao la bona, mme ba bea balebedi."
		},
		"28": {
			"1": "Eitse qetellong ya Sabatha, ha letsatsi le qalang veke le tla tjhaba, Maria Magdalena le Maria e mong ba tla bona lebitla.",
			"2": "Tshisinyeho e kgolo ya lefatshe ya eba teng; hobane lengeloi la Morena la theoha lehodimong, la tla, la phikolosa lejwe le neng le le monyako, la ntoo dula hodima lona.",
			"3": "Sefahleho sa lona se ne se le jwaloka lehadima, seaparo sa lona se le sesweu jwaloka lehlwa.",
			"4": "Balebedi ba thothomela ke ho le tshoha, ba eba jwaloka bafu.",
			"5": "Lengeloi la bua, la re ho basadi: Haele lona, le se ke la tshoha, hobane ke a tseba hoba le batla Jesu, ya kileng a thakgiswa.",
			"6": "Ha a sa le mona, hobane o tsohile jwalokaha a ne a boletse. Tloong, le bone tao eo Morena a neng a beilwe ho yona.",
			"7": "Le mpe le phakise le ye ho bolella barutuwa ba hae hore o tsohile bafung; bonang, o ya le etella pele ho ya Galelea; le tla mmona teng. Utlwang, ke le boleletse.",
			"8": "Ba phakisa ba tloha lebitleng ka tshabo le ka thabo e kgolo, ba matha, ba ya bolella barutuwa ba hae.",
			"9": "Yare ba sa ya bolella barutuwa ba hae, ba bona Jesu a ba kgahlanyetsa, a re: Dumelang! Yaba ba tla, ba mo tshwara maoto, ba mo kgumamela.",
			"10": "Jwale, Jesu a re ho bona: Se tshoheng; eyang, le bolelle banabeso hore ba ye Galelea; ke moo ba tla mpona teng.",
			"11": "Hobane ba tlohe, ba bang ho balebedi ba tla hae motseng, ho behela baprista ba baholo tsohle tse hlahileng.",
			"12": "Baprista eitse hobane ba phuthehe le baholo, ba etsa morero, mme ba nea bahlabani ditjhelete tse ngata;",
			"13": "ba re: Ereng: Barutuwa ba hae ba tlile bosiu, ba mo utswitse re robetse.",
			"14": "Mme leha mmusisi a utlwa taba eo, ke rona re tla mo kgodisa, re le sireletse.",
			"15": "Yaba balebedi ba nka ditjhelete, ba etsa kamoo ba laetsweng kateng; mme taba eo e tumile hara Bajuda ho fihlela kajeno.",
			"16": "Barutuwa ba leshome le motso o mong ba ya Galelea, thabeng eo ba e laetsweng ke Jesu.",
			"17": "Ha ba mmona, ba mo kgumamela; empa ba bang ba belaela.",
			"18": "Yaba Jesu o atamela, a bua le bona, a re: Ke neilwe borena bohle lehodimong le lefatsheng.",
			"19": "Eyang ke hona, le rute ditjhaba tsohle; le ba kolobetse ka lebitso la Ntate, le la Mora, le la Moya o Halalelang;",
			"20": "mme le ba rute ho boloka tsohle tseo ke le laetseng tsona. Mme bonang, nna, ke na le lona kamehla yohle, ho isa bofelong ba lefatshe."
		}
	}
};
module.exports = book;